Method.Source:
    [Form]
    public class EcoResProductReleaseSessions extends FormRun
    {
        EcoResProductReleaseSessionsForm    productReleaseSessionsForm;

}

public void init()
{
    productReleaseSessionsForm  = EcoResProductReleaseSessionsForm::construct();

    super();

    this.setupFallbackProductTranslation();

    productReleaseSessionsForm.parmReleaseSessionFormDataSource(ecoResReleaseSession_ds);
    productReleaseSessionsForm.parmSelectSessionFormDataSource(ecoResReleaseSessionProductSelectSession_ds);
    productReleaseSessionsForm.parmProductFormDataSource(ecoResProduct_ds);
    productReleaseSessionsForm.parmCompanyInfoFormDataSource(companyInfo_ds);
    productReleaseSessionsForm.parmProductVariantFormDataSource(ecoResDistinctProductVariant_ds);
    productReleaseSessionsForm.parmMenuButtonDetailsExtended(ecoResProductDetailsExtended);
    productReleaseSessionsForm.parmMenuButtonVariantsPerCompany(ecoResProductVariantsPerCompany);
    productReleaseSessionsForm.init();

    productReleaseSessionsForm.formInit();
}

private void setupFallbackProductTranslation()
{
    List fallbackStringControls = new List(Types::Integer);
    List ecoResProductTranslationStringControls = new List(Types::Integer);

    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductReleaseSessions, EcoResProductTranslation_Name)));
    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductReleaseSessions, Details_EcoResProductTranslation_Name)));

    fallbackStringControls.addEnd(element.controlId(formControlStr(EcoResProductReleaseSessions, EcoResProductTranslationFallbackView_Name)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(EcoResProductReleaseSessions, Details_EcoResProductTranslationFallbackView_Name)));

    EcoResProductTranslationFallbackViewModel::setupFallbackTranslationForm(element,
                                                                            element.EcoResProductTranslationFallbackView_ds,
                                                                            element.EcoResProduct_ds,
                                                                            fallbackStringControls,
                                                                            ecoResProductTranslationStringControls);
}

/// <summary>
/// Delete the whole release session.
/// </summary>
public void clicked()
{
    productReleaseSessionsForm.deleteSession();
}
