/// <param name = "_productName">The product variant name to set.</param>
/// <returns>
///    The variant name.
/// </returns>
public edit EcoResProductName editTranslationFallbackName(boolean _set, EcoResProductName _productName)
{
    if (element.productVariantsFormHelper().isNewProductVariant())
    {
        if (_set)
        {
            ecoResProductTranslation.Name = _productName;
        }

        return ecoResProductTranslation.Name;
    }

    return ecoResProductTranslationFallbackView.Name;
}

