/// <summary>
/// Adds a set of metadata related to an inventory dimension.
/// </summary>
/// <param name = "_inventDimFieldId">The dimensions field Id in InventDim table.</param>
/// <param name = "_productVariantDimensionFieldId">The dimension field id in product variant table.</param>
/// <param name = "_productVariantDimensionRetailWeightFieldId">The dimension retail weight field id in product variant table.</param>
public void addInventDimensionMetadata(
    FieldId _inventDimFieldId,
    FieldId _productVariantDimensionFieldId,
    FieldId _productVariantDimensionRetailWeightFieldId = 0)
{
    this.productVariantsFormHelper().addInventDimensionMetadata(_inventDimFieldId, _productVariantDimensionFieldId);

    if (_productVariantDimensionRetailWeightFieldId)
    {
        TableId dimensionTableId = ecoResProductDimensionDataModel.inventDimFieldId2DimensionTableId(_inventDimFieldId);
        inventTableIdProductVariantDimensionRetailWeightFieldIdMap.insert(dimensionTableId, _productVariantDimensionRetailWeightFieldId);
    }
}

/// <summary>
/// Initializes maps with dimensions metadata that the form uses.
/// </summary>
protected void initInventDimensionMetadataEntries()
{
    this.addInventDimensionMetadata(
        InventDim::fieldIdColor(),
        fieldNum(EcoResProductVariantColor, Color),
        fieldNum(EcoResProductVariantColor, RetailWeight));

    this.addInventDimensionMetadata(
        InventDim::fieldIdSize(),
        fieldNum(EcoResProductVariantSize, Size),
        fieldNum(EcoResProductVariantSize, RetailWeight));

    this.addInventDimensionMetadata(
        InventDim::fieldIdStyle(),
        fieldNum(EcoResProductVariantStyle, Style),
        fieldNum(EcoResProductVariantStyle, RetailWeight));

    this.addInventDimensionMetadata(
        fieldNum(InventDim, configId),
        fieldNum(EcoResProductVariantConfiguration, Configuration));

    if (isEcoResProductVersionFeatureEnabled)
    {
        this.addInventDimensionMetadata(
            InventDim::fieldIdProductVersion(),
            fieldNum(EcoResProductVariantVersion, ProductVersion));
    }
}

public void calcRetailTotalWeightForDimensions(container _dimensions)
{
    int                                 dimensionIndex;
    container                           dimensionValues;
    RefRecId                            productDimensionAttribute;

    ecoResDistinctProductVariant.RetaiTotalWeight = 0;
    for (dimensionIndex = conLen(_dimensions); dimensionIndex > 0; --dimensionIndex)
    {
        dimensionValues = conpeek(_dimensions, dimensionIndex);
        productDimensionAttribute = conPeek(dimensionValues, 1);

        TableId dimensionTableId = EcoResProductDimensionAttribute::find(productDimensionAttribute).getDimensionTableId();
        FormDataSource productVariantDimension_ds = this.findFirstFormDataSource(ecoResProductDimensionDataModel.dimensionTableId2VariantDimensionTableId(dimensionTableId));

        if (productVariantDimension_ds
            && inventTableIdProductVariantDimensionRetailWeightFieldIdMap.exists(dimensionTableId))
        {
            Common ecoResProductVariantDimension = productVariantDimension_ds.cursor();
            FieldId productVariantDimensionRetailWeightFieldId = inventTableIdProductVariantDimensionRetailWeightFieldIdMap.lookup(dimensionTableId);
            ecoResDistinctProductVariant.RetaiTotalWeight = (ecoResDistinctProductVariant.RetaiTotalWeight ? ecoResDistinctProductVariant.RetaiTotalWeight : 1)* (ecoResProductVariantDimension.(productVariantDimensionRetailWeightFieldId) ? ecoResProductVariantDimension.(productVariantDimensionRetailWeightFieldId) : 1);
        }
    }
}

/// <summary>
/// Return configuration for Product Variants Display Sorting Disabled.
/// </summary>
/// <returns>True when sorting is disabled. False otherwise.</returns>
internal boolean isDisplaySortingDisabled()
{
    return (isConfigurationkeyEnabled(configurationKeyNum(Retail)) ?
        RetailConfigurationParameters::getProductVariantsDisplaySortingDisabledFlag() :
        false);
}

public void init()
{
    EcoResProductVariantsFormHelper productVariantsFormHelperLocal = this.productVariantsFormHelper();

    useEcoResProductVariantDisplayOrderView = RetailConfigurationParameters::getUseProductVariantDisplayOrderViewFlag();
    isEcoResProductVariantDisplaySortingDisabled = this.isDisplaySortingDisabled();
    isEcoResProductVersionFeatureEnabled = isConfigurationkeyEnabled(configurationKeyNum(EcoResProductVersion));
    isFallbackFeatureAndParameterEnabled =
        FeatureStateProvider::isFeatureEnabled(EcoResProductTranslationFallbackFeature::instance())
        && EcoResProductParameters::find().UseFallbackTranslation;

    super();

    this.disableVersionDatasourceIfDimensionNotEnabled();

    this.initInventDimensionMetadataEntries();

    productVariantsFormHelperLocal.initProductDimensionsDatasources();
    productVariantsFormHelperLocal.setProductDimensionsVisible();

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)) &&
        productVariantsFormHelperLocal.parmIsOpenedForSingleProduct())
    {
        EcoResProductVariantsFormHelper formHelper = this.productVariantsFormHelper();

        Map dimensionVariantTableIdIsActiveForRetailMap = formHelper.dimensionVariantTableIdIsActiveForRetailMap();
        formHelper.updateRetailSupportedVariantDimentsionBoundControlsVisibility(ReplenishmentTabPage, dimensionVariantTableIdIsActiveForRetailMap);
        boolean isAnyRetailDimensionActive = dimensionVariantTableIdIsActiveForRetailMap.valueSet().in(true);

        EcoResDistinctProductVariant_RetaiTotalWeight.visible(isAnyRetailDimensionActive);
        DetailsHeader_EcoResDistinctProductVariant_RetaiTotalWeight.visible(isAnyRetailDimensionActive);

        // Do not allow editing of Variants for Kits because they are auto-generated.
        if (element.args().record() is EcoResProductMaster)
        {
            EcoResProductMaster productMasterCaller = element.args().record() as EcoResProductMaster;
            boolean isKit = RetailKit::existByProductMaster(productMasterCaller.RecId);

            if (isKit)
            {
                element.makeDataSourceReadOnly(EcoResDistinctProductVariant_ds);
                productVariantsFormHelperLocal.parmIsRetailKit(isKit);
            }
        }
    }

    this.setButtonsEnabled();

    if (!useEcoResProductVariantDisplayOrderView && !isEcoResProductVariantDisplaySortingDisabled)
    {
        int dsCount = this.dataSourceCount();

        for (int dsIndex = 1; dsIndex <= dsCount; ++dsIndex)
        {
            this.addProductVariantNameSort(this.dataSource(dsIndex));
        }
    }

    // Adding Retail filters to display assorted product variants.
    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)) && element.args().parmObject())
    {
        this.addRetailFilters(element.args().parmObject());
    }

    this.setupFallbackProductTranslation();
}

private void setupFallbackProductTranslation()
{
    List fallbackStringControls = new List(Types::Integer);
    List ecoResProductTranslationStringControls = new List(Types::Integer);

    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductVariants, EcoResProductTranslation_NameListGrid)));
    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductVariants, EcoResProductTranslation_Name)));
    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductVariants, EcoResProductTranslation_NameGridVariants)));
    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductVariants, EcoResProductTranslation_Description)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(EcoResProductVariants, EcoResProductTranslationFallbackView_Name)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(EcoResProductVariants, EcoResProductTranslationFallbackView_NameMainGrid)));

    EcoResProductTranslationFallbackViewModel::setupFallbackTranslationForm(element,
                                                                            element.EcoResProductTranslationFallbackView_ds,
                                                                            element.EcoResDistinctProductVariant_ds,
                                                                            fallbackStringControls,
                                                                            ecoResProductTranslationStringControls);
}

private void addRetailFilters(Args _callerArgs)
{
    Common callerRecord = _callerArgs.record();
    QueryBuildDataSource qbdsRetailAssortmentLookupExploded, qbdsRetailAssortmentLookupChannelGroup, qbdsRetailChannelTable, qbdsRetailAssortmentLookupVariant;

    switch (callerRecord.TableId)
    {
        case tableNum(OMOperatingUnit) :
            // Called from Retail channels form to display assorted product variants for given Retail channel.
            qbdsRetailAssortmentLookupExploded = EcoResDistinctProductVariant_ds.query().dataSourceNo(1).addDataSource(tableNum(RetailAssortmentLookupExploded));
            qbdsRetailAssortmentLookupExploded.relations(false);
            qbdsRetailAssortmentLookupExploded.joinMode(JoinMode::ExistsJoin);
            qbdsRetailAssortmentLookupExploded.addLink(fieldNum(EcoResDistinctProductVariant, RecId), fieldNum(RetailAssortmentLookupExploded, Variant));

            qbdsRetailAssortmentLookupChannelGroup = qbdsRetailAssortmentLookupExploded.addDataSource(tableNum(RetailAssortmentLookupChannelGroup));
            qbdsRetailAssortmentLookupChannelGroup.relations(false);
            qbdsRetailAssortmentLookupChannelGroup.joinMode(JoinMode::ExistsJoin);
            qbdsRetailAssortmentLookupChannelGroup.addLink(fieldNum(RetailAssortmentLookupExploded, AssortmentId), fieldNum(RetailAssortmentLookupChannelGroup, AssortmentId));
            qbdsRetailAssortmentLookupChannelGroup.addRange(fieldNum(RetailAssortmentLookupChannelGroup, OMOperatingUnitId)).value(queryValue(callerRecord.RecId));

            qbdsRetailChannelTable = qbdsRetailAssortmentLookupChannelGroup.addDataSource(tableNum(RetailChannelTable));
            qbdsRetailChannelTable.relations(false);
            qbdsRetailChannelTable.joinMode(JoinMode::ExistsJoin);
            qbdsRetailChannelTable.addLink(fieldNum(RetailAssortmentLookupChannelGroup, OMOperatingUnitId), fieldNum(RetailChannelTable, OMOperatingUnitID));

            // Using RetailAssortmentLookup to exclude records with LineType as Exclude.
            qbdsRetailAssortmentLookupVariant = qbdsRetailAssortmentLookupExploded.addDataSource(tableNum(RetailAssortmentLookup));
            qbdsRetailAssortmentLookupVariant.relations(false);
            qbdsRetailAssortmentLookupVariant.joinMode(JoinMode::NoExistsJoin);
            qbdsRetailAssortmentLookupVariant.addLink(fieldNum(RetailAssortmentLookupExploded, Product), fieldNum(RetailAssortmentLookup, VariantId));
            qbdsRetailAssortmentLookupVariant.addRange(fieldNum(RetailAssortmentLookup, LineType)).value(queryValue(RetailAssortmentExcludeIncludeType::Exclude));

            QueryBuildDataSource qbdsRetailAssortmentVariantLookupChannelGroup = qbdsRetailAssortmentLookupVariant.addDataSource(tableNum(RetailAssortmentLookupChannelGroup));
            qbdsRetailAssortmentVariantLookupChannelGroup.relations(false);
            qbdsRetailAssortmentVariantLookupChannelGroup.joinMode(JoinMode::ExistsJoin);
            qbdsRetailAssortmentVariantLookupChannelGroup.addLink(fieldNum(RetailAssortmentLookup, AssortmentId), fieldNum(RetailAssortmentLookupChannelGroup, AssortmentId));
            qbdsRetailAssortmentVariantLookupChannelGroup.addRange(fieldNum(RetailAssortmentLookupChannelGroup, OMOperatingUnitId)).value(queryValue(callerRecord.RecId));

            QueryBuildDataSource qbdsRetailVariantChannelTable = qbdsRetailAssortmentVariantLookupChannelGroup.addDataSource(tableNum(RetailChannelTable));
            qbdsRetailVariantChannelTable.relations(false);
            qbdsRetailVariantChannelTable.joinMode(JoinMode::ExistsJoin);
            qbdsRetailVariantChannelTable.addLink(fieldNum(RetailAssortmentLookupChannelGroup, OMOperatingUnitId), fieldNum(RetailChannelTable, OMOperatingUnitID));
            break;

        case tableNum(RetailAssortmentTable) :
            // Called from Retail assortments form to display assorted product variants for given Retail assortment.
            qbdsRetailAssortmentLookupExploded = EcoResDistinctProductVariant_ds.query().dataSourceNo(1).addDataSource(tableNum(RetailAssortmentLookupExploded));
            qbdsRetailAssortmentLookupExploded.relations(false);
            qbdsRetailAssortmentLookupExploded.joinMode(JoinMode::ExistsJoin);
            qbdsRetailAssortmentLookupExploded.addLink(fieldNum(EcoResDistinctProductVariant, RecId), fieldNum(RetailAssortmentLookupExploded, Variant));
            qbdsRetailAssortmentLookupExploded.addRange(fieldNum(RetailAssortmentLookupExploded, AssortmentId)).value(queryValue(callerRecord.RecId));

            // Using RetailAssortmentLookup to only include records with LineType as Include.
            qbdsRetailAssortmentLookupVariant = qbdsRetailAssortmentLookupExploded.addDataSource(tableNum(RetailAssortmentLookup));
            qbdsRetailAssortmentLookupVariant.relations(false);
            qbdsRetailAssortmentLookupVariant.joinMode(JoinMode::NoExistsJoin);
            qbdsRetailAssortmentLookupVariant.addLink(fieldNum(RetailAssortmentLookupExploded, Product), fieldNum(RetailAssortmentLookup, VariantId));
            qbdsRetailAssortmentLookupVariant.addRange(fieldNum(RetailAssortmentLookup, LineType)).value(queryValue(RetailAssortmentExcludeIncludeType::Exclude));
            qbdsRetailAssortmentLookupVariant.addRange(fieldNum(RetailAssortmentLookup, AssortmentId)).value(queryValue(callerRecord.RecId));
            break;
    }
}

private void disableVersionDatasourceIfDimensionNotEnabled()
{
    if (!isConfigurationkeyEnabled(configurationKeyNum(EcoResProductVersion)))
    {
        EcoResDistinctProductVariant_ds.query().dataSourceName(formDataSourceStr(EcoResProductVariants, EcoResProductVariantVersion)).enabled(false);
    }
}

[Wrappable(true)]
protected final void addProductVariantNameSort(FormDataSource _formDataSource)
{
    if (_formDataSource.table() == tablenum(EcoResColor))
    {
        QueryBuildDataSource qbs = _formDataSource.queryBuildDataSource();
        qbs.addSortField(fieldnum(EcoResColor,Name));
    }
    else if (_formDataSource.table() == tablenum(EcoResSize))
    {
        QueryBuildDataSource qbs = _formDataSource.queryBuildDataSource();
        qbs.addSortField(fieldnum(EcoResSize,Name));
    }
    else if (_formDataSource.table() == tablenum(EcoResStyle))
    {
        QueryBuildDataSource qbs = _formDataSource.queryBuildDataSource();
        qbs.addSortField(fieldnum(EcoResStyle,Name));
    }
    else if (isEcoResProductVersionFeatureEnabled && _formDataSource.table() == tablenum(EcoResVersion))
    {
        QueryBuildDataSource qbs = _formDataSource.queryBuildDataSource();
        qbs.addSortField(fieldnum(EcoResVersion,Name));
    }
}

