public void setNameAndProductNumberFromNomenclature()
{
    if(InventDistinctProductValidator::construct().isDistinctProductVariantFullySpecifiedByInventDim(EcoResDistinctProductVariant.ProductMaster, this.createInventDimFromDatasources()))
    {
        EcoResProductMaster currProductMaster = element.productVariantsFormHelper().parmEcoResProductMaster();

        container dimensions =  element.productVariantsFormHelper().getProductDimensionValues();

        ecoResDistinctProductVariant.DisplayProductNumber = EcoResProductNumberBuilderVariant::buildFromProductMasterAndDimensions(
                currProductMaster,
                dimensions);

        if(ecoResProductTranslation.Name == '' && ecoResDistinctProductVariant.SearchName == '')
        {
            ecoResProductTranslation.Name = EcoResProductNumberBuilderVariant::buildNameFromProductMasterAndDimensions(
                currProductMaster,
                dimensions);

            ecoResDistinctProductVariant.SearchName = '';
            ecoResDistinctProductVariant.setSearchName(ecoResProductTranslation.Name);
        }
    }
}

public void init()
{
    super();

    if (element.productVariantsFormHelper().parmIsOpenedForSingleProduct())
    {
        this.object(fieldNum(EcoResDistinctProductVariant, ProductMaster)).visible(false);

        element.productVariantsFormHelper().initProductVariantDataSource(this.queryBuildDataSource());
    }
    else
    {
        this.allowCreate(false);
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        QueryBuildDataSource qbdsProductMaster;

        // join to EcoResProductMaster table to sort by DisplayProductNumber
        qbdsProductMaster = this.queryBuildDataSource().addDataSource(tableNum(EcoResProductMaster));
        qbdsProductMaster.joinMode(JoinMode::InnerJoin);

        // disable existing relation because the relation is from EcoResDistinctProductVariant to EcoResProductMaster, and if use default relation there won't be any record displayed
        qbdsProductMaster.relations(false);
        qbdsProductMaster.addLink(fieldNum(EcoResDistinctProductVariant, ProductMaster),  fieldNum(EcoResProductMaster, RecId));

        if (!isEcoResProductVariantDisplaySortingDisabled)
        {
            qbdsProductMaster.addOrderByField(fieldNum(EcoResProductMaster, DisplayProductNumber));
        }
    }
}

public void writing()
{
    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail))
        && element.productVariantsFormHelper().isNewProductVariant())
    {
        container dimensions = element.productVariantsFormHelper().getProductDimensionValues();
        element.calcRetailTotalWeightForDimensions(dimensions);
    }
}

