{
    super();

    if (!useEcoResProductVariantDisplayOrderView)
    {
        if (!isEcoResProductVariantDisplaySortingDisabled)
        {
            this.queryBuildDataSource().addOrderByField(fieldNum(EcoResProductVariantSize, RetailDisplayOrder));
        }
