public void modified()
{
    EcoResProductMasterSize ecoResProductMasterSize;

    element.setNameAndProductNumberFromNomenclature();

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        select firstonly RetailWeight from ecoResProductMasterSize
            where ecoResProductMasterSize.Size == ecoResProductVariantSize.Size &&
                  ecoResProductMasterSize.SizeProductMaster == ecoResDistinctProductVariant.ProductMaster;

        ecoResProductVariantSize.RetailWeight = ecoResProductMasterSize.RetailWeight;
    }
}

