public void initValue()
{
    if (element.productVariantsFormHelper().isDimensionDatasourceActive(this))
    {
        ecoResProductVariantColor.ProductDimensionAttribute = EcoResProductDimensionAttribute::inventDimFieldId2DimensionAttributeRecId(fieldNum(InventDim, InventColorId));
    }

    super();
}

public boolean validateWrite()
{
    //don't bother if dimension is not active
    if (!element.productVariantsFormHelper().isDimensionDatasourceActive(this))
    {
        return true;
    }

    return super();
}

public void init()
{
    super();

    if (!useEcoResProductVariantDisplayOrderView)
    {
        if (!isEcoResProductVariantDisplaySortingDisabled)
        {
            this.queryBuildDataSource().addOrderByField(fieldNum(EcoResProductVariantColor, RetailDisplayOrder));
        }
    }
}

public void modified()
{
    EcoResProductMasterColor ecoResProductMasterColor;

    element.setNameAndProductNumberFromNomenclature();

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        select firstonly RetailWeight from ecoResProductMasterColor
            where ecoResProductMasterColor.Color == ecoResProductVariantColor.Color &&
                  ecoResProductMasterColor.ColorProductMaster == ecoResDistinctProductVariant.ProductMaster;

        ecoResProductVariantColor.RetailWeight = ecoResProductMasterColor.RetailWeight;
    }
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    EcoResColorName colorName;

    // Don't call super.

    colorName = _formReferenceControl.filterValue(AbsoluteFieldBinding::construct(fieldStr(EcoResColor, Name), tableStr(EcoResColor))).value();

    return EcoResColor::resolveProductVariantColor(colorName,
        element.productVariantsFormHelper().parmEcoResProductMaster().RecId,
        ecoResProductVariantColor.ProductDimensionAttribute);
}

public void jumpRef()
{
    element.productVariantsFormHelper().handleProductDimensionJumpRef(
        ecoResProductVariantColor.ProductDimensionAttribute);
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret;

    // Don't call super.

    ret = EcoResColor::lookupProductVariantColor(_formReferenceControl,
        element.productVariantsFormHelper().parmEcoResProductMaster().RecId,
        ecoResProductVariantColor.ProductDimensionAttribute);

    return ret;
}

public void init()
{
    super();

    EcoResProductTranslation::queryAddDefaultLanguage(this.query(), this.name());

    if (!isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        if (!isEcoResProductVariantDisplaySortingDisabled)
        {
            this.query().dataSourceName(this.name()).addOrderByField(fieldNum(EcoResProductTranslation, Name));
        }
    }
}

public void initValue()
{
    ecoResProductTranslation.setDefaultLanguage();

    super();
}

public boolean validateWrite()
{
    boolean     ret = true;

    if (!element.productVariantsFormHelper().isNewProductVariant())
    {
        if (!ecoResProductTranslation.Product || !ecoResProductTranslation.LanguageId)
        {
            ecoResProductTranslation.Product = ecoResDistinctProductVariant.RecId;        //Initialize in order to avoid validateWrite to fail.
            ecoResProductTranslation.setDefaultLanguage();                               //Initialize in order to avoid validateWrite to fail.
        }

        if (ecoResProductTranslation.canMaintainTranslation())
        {
            ret = super();
        }
    }

    return ret;
}

public void modified()
{
    super();

    if (ecoResProductTranslation.Name)
    {
        ecoResDistinctProductVariant.setSearchName(ecoResProductTranslation.Name);
    }
}

public int active()
{
    boolean isNewProductVariant = element.productVariantsFormHelper().isNewProductVariant();
    EcoResProductTranslationFallbackName.allowEdit(isNewProductVariant);
    EcoResProductTranslationFallbackDescription.allowEdit(isNewProductVariant);

    return super();
}

