/// <summary>
/// Gets the set of initial filters to be applied to the template.
/// </summary>
/// <returns>
/// The Map of filters to apply.
/// Keyed by AOT entity name.
/// Values are all Microsoft.Dynamics.Platform.Integration.Office.FilterNode instances.
/// </returns>
/// <remarks>
/// The filters supplied here will override any filters currently in the template.
/// If no filter is supplied for a given entity, any filters currently in the template are retained.
/// </remarks>
public Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem)
{
    Map filtersToApply = new Map(Types::String, Types::Class);

    DocuTemplate template = DocuTemplate::findTemplate(_menuItem.applicationType(), _menuItem.templateId());

    if (_menuItem.dataEntityName() == dataentityviewstr(EcoResReleasedProductVariantV2Entity)
        && template
        && template.ApplyRecordFilter)
    {
        ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(EcoResReleasedProductVariantV2Entity));

        EcoResProductDisplayProductNumber productMasterNumber = EcoResProduct::find(EcoResDistinctProductVariant.ProductMaster).DisplayProductNumber;

        InventDim productDimensions = InventDim::find(InventDimCombination.InventDimId);

        var filter = filterBuilder.and(
                filterBuilder.areEqual(fieldStr(EcoResReleasedProductVariantV2Entity, DataAreaId), curExt()),
                filterBuilder.and(filterBuilder.areEqual(fieldStr(EcoResReleasedProductVariantV2Entity, ProductMasterNumber), productMasterNumber),
                filterBuilder.and(filterBuilder.areEqual(fieldStr(EcoResReleasedProductVariantV2Entity, ProductConfigurationId), productDimensions.configId),
                filterBuilder.and(filterBuilder.areEqual(fieldStr(EcoResReleasedProductVariantV2Entity, ProductSizeId), productDimensions.InventSizeId),
                filterBuilder.and(filterBuilder.areEqual(fieldStr(EcoResReleasedProductVariantV2Entity, ProductStyleId), productDimensions.InventStyleId),
                filterBuilder.and(filterBuilder.areEqual(fieldStr(EcoResReleasedProductVariantV2Entity, ProductColorId), productDimensions.InventColorId),
                filterBuilder.areEqual(fieldStr(EcoResReleasedProductVariantV2Entity, ProductVersionId), productDimensions.InventVersionId)))))));

        filtersToApply.insert(dataentityviewstr(EcoResReleasedProductVariantV2Entity), filter);

        // A record filter has already been applied
        _menuItem.applyRecordFilter(false);
    }

    return filtersToApply;
}

