/// <summary>
/// Gets the DisplayOrder value of the dimension used by _formGroupControl.
/// </summary>
/// <param name = "_formGroupControl">The form group control of the dimension column.</param>
/// <param name = "_productVariantsFormHelperLocal">The local form helper.</param>
/// <returns>The DisplayOrder value of the dimension.</returns>
private real getDimensionFormGroupControlDisplayOrder(FormGroupControl _formGroupControl, EcoResProductVariantsFormHelper _productVariantsFormHelperLocal)
{
    FormDataSource dataSourceObject = _formGroupControl.dataSourceObject();
    TableId dimensionTableId = EcoResProductDimensionAttribute::variantDimensionTableId2DimensionTableId(dataSourceObject.table());
    FieldId dimFieldId = EcoResProductDimensionAttribute::dimensionTable2InventDimFieldId(dimensionTableId);
    real dimensionDisplayOrder = _productVariantsFormHelperLocal.productDimensionDisplayOrder(dimFieldId);

    return dimensionDisplayOrder;
}

/// <summary>
/// Sorts the columns of dimensions by their DisplayOrder value.
/// </summary>
/// <param name = "_productVariantsFormHelperLocal">The local form helper.</param>
private void sortProductDimensionsByDisplayOrder(EcoResProductVariantsFormHelper _productVariantsFormHelperLocal)
{
    Map  displayOrderDimensionColumnIdMap = new Map(Types::Real, Types::Class);
    List dimensionFormGroupControlList    = new List(Types::Class);

    dimensionFormGroupControlList.addEnd(SetupSizeMainGrid);
    dimensionFormGroupControlList.addEnd(SetupConfigMainGrid);
    dimensionFormGroupControlList.addEnd(SetupColorMainGrid);
    dimensionFormGroupControlList.addEnd(SetupStyleMainGrid);
    dimensionFormGroupControlList.addEnd(SetupProductVersionMainGrid);

    var dimensionFormGroupControlListEnumerator = dimensionFormGroupControlList.getEnumerator();
    while (dimensionFormGroupControlListEnumerator.moveNext())
    {
        real displayOrder = this.getDimensionFormGroupControlDisplayOrder(dimensionFormGroupControlListEnumerator.current(), _productVariantsFormHelperLocal);
        if (!displayOrderDimensionColumnIdMap.exists(displayOrder))
        {
            displayOrderDimensionColumnIdMap.insert(displayOrder, new List(Types::Integer));
        }
        List formGroupControlIdList = displayOrderDimensionColumnIdMap.lookup(displayOrder);
        FormGroupControl formGroupControl = dimensionFormGroupControlListEnumerator.current();
        formGroupControlIdList.addEnd(formGroupControl.id());
    }

    int currentColumnId;
    int prevColumnId                               = DistinctProductVariant_SearchNameMainGrid.id();
    var displayOrderDimensionColumnIdMapEnumerator = displayOrderDimensionColumnIdMap.getEnumerator();
    while (displayOrderDimensionColumnIdMapEnumerator.moveNext())
    {
        List formGroupControlIdList = displayOrderDimensionColumnIdMapEnumerator.currentValue();
        var  formGroupControlIdListEnumerator = formGroupControlIdList.getEnumerator();
        while (formGroupControlIdListEnumerator.moveNext())
        {
            currentColumnId = formGroupControlIdListEnumerator.current();
            MainGrid.moveControl(currentColumnId, prevColumnId);
            prevColumnId = currentColumnId;
        }
    }

}

