public void init()
{
    EcoResProductVariantsFormHelper productVariantsFormHelperLocal = this.productVariantsFormHelper();
    EcoResProductMaster productMasterCaller;
    boolean isKit;

    useEcoResProductVariantDisplayOrderView = RetailConfigurationParameters::getUseProductVariantDisplayOrderViewFlag();
    isEcoResProductVariantDisplaySortingDisabled = this.isDisplaySortingDisabled();
    isEcoResProductVersionFeatureEnabled = isConfigurationkeyEnabled(configurationKeyNum(EcoResProductVersion));
    isFallbackFeatureAndParameterEnabled = 
        FeatureStateProvider::isFeatureEnabled(EcoResProductTranslationFallbackFeature::instance()) 
        && EcoResProductParameters::find().UseFallbackTranslation;


    super();

    this.initInventDimensionMetadataEntries();

    productVariantsFormHelperLocal.initProductDimensionsDatasources();
    productVariantsFormHelperLocal.setProductDimensionsVisible();

    if (FeatureStateProvider::isFeatureEnabled(RetailEnableDisplayOrderForMerchandisingFeature::instance()))
    {
        this.sortProductDimensionsByDisplayOrder(productVariantsFormHelper);
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail))
        && productVariantsFormHelperLocal.parmIsOpenedForSingleProduct())
    {
        EcoResProductVariantsCompanyHelper formHelper = this.productVariantsFormHelper();

        Map dimensionVariantTableIdIsActiveForRetailMap = formHelper.dimensionVariantTableIdIsActiveForRetailMap();
        formHelper.updateRetailSupportedVariantDimentsionBoundControlsVisibility(ReplenishmentTabPage, dimensionVariantTableIdIsActiveForRetailMap);

        boolean isAnyRetailDimensionActive = dimensionVariantTableIdIsActiveForRetailMap.valueSet().in(true);
        EcoResDistinctProductVariant_RetaiTotalWeight.visible(isAnyRetailDimensionActive);
        DetailsHeader_EcoResDistinctProductVariant_RetaiTotalWeight.visible(isAnyRetailDimensionActive);

        if (element.args().record() is EcoResProductMaster)
        {
            productMasterCaller = element.args().record() as EcoResProductMaster;
            isKit = RetailKit::existByProductMaster(productMasterCaller.RecId);

            if (isKit)
            {
                element.makeDataSourceReadOnly(InventDimCombination_ds);
                productVariantsFormHelperLocal.parmIsRetailKit(isKit);
            }
        }
    }

    this.setButtonsEnabled();

    if (!useEcoResProductVariantDisplayOrderView && !isEcoResProductVariantDisplaySortingDisabled)
    {
        int dsCount = this.dataSourceCount();

        for (int dsIndex = 1; dsIndex <= dsCount; ++dsIndex)
        {
            this.addProductVariantNameSort(this.dataSource(dsIndex));
        }
    }

    this.setupFallbackProductTranslation();
}

private void setupFallbackProductTranslation()
{
    List fallbackStringControls = new List(Types::Integer);
    List ecoResProductTranslationStringControls = new List(Types::Integer);

    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductVariantsPerCompany, EcoResProductTranslation_Name)));
    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductVariantsPerCompany, EcoResProductTranslation_NameDetails)));
    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductVariantsPerCompany, EcoResProductTranslation_NameMainGrid)));
    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(EcoResProductVariantsPerCompany, EcoResProductTranslation_Description)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(EcoResProductVariantsPerCompany, EcoResProductTranslationFallbackView_Name)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(EcoResProductVariantsPerCompany, EcoResProductTranslationFallbackView_NameMainGrid)));

    EcoResProductTranslationFallbackViewModel::setupFallbackTranslationForm(element,
                                                                            element.EcoResProductTranslationFallbackView_ds,
                                                                            element.EcoResDistinctProductVariant_ds,
                                                                            fallbackStringControls,
                                                                            ecoResProductTranslationStringControls);
}

[Wrappable(true)]
protected final void addProductVariantNameSort(FormDataSource _formDataSource)
{
    if (_formDataSource.table() == tablenum(EcoResColor))
    {
        QueryBuildDataSource qbs = _formDataSource.queryBuildDataSource();
        qbs.addSortField(fieldnum(EcoResColor,Name));
    }
    else if (_formDataSource.table() == tablenum(EcoResSize))
    {
        QueryBuildDataSource qbs = _formDataSource.queryBuildDataSource();
        qbs.addSortField(fieldnum(EcoResSize,Name));
    }
    else if (_formDataSource.table() == tablenum(EcoResStyle))
    {
        QueryBuildDataSource qbs = _formDataSource.queryBuildDataSource();
        qbs.addSortField(fieldnum(EcoResStyle,Name));
    }
    else if (isEcoResProductVersionFeatureEnabled && _formDataSource.table() == tablenum(EcoResVersion))
    {
        QueryBuildDataSource qbs = _formDataSource.queryBuildDataSource();
        qbs.addSortField(fieldnum(EcoResVersion,Name));
    }
}

/// <summary>
///     Performs the write operation for the <c>InventDimCombination</c> data source.
/// </summary>
protected void inventDimCombinationWrite()
{
    EcoResProductReleaseManagerBase     releaseManager;
    InventDimCombination                inventDimCombinationReleased;

    if (element.productVariantsFormHelper().isNewProductVariant())
    {
        ttsbegin;

        // create product variant and related tables
        ecoResDistinctProductVariant.write();

        // create product variant dimension values table, if applicable
        element.productVariantsFormHelper().createProductDimValueRecords(ecoResDistinctProductVariant);

        // create product translation
        if (ecoResProductTranslation.canMaintainTranslation())
        {
            ecoResProductTranslation.Product = ecoResDistinctProductVariant.RecId;
            ecoResProductTranslation.write();
        }

        //release product variant
        releaseManager = EcoResProductReleaseManagerBase::newFromProduct(ecoResDistinctProductVariant);
        releaseManager.parmDefaultProductLifecycleState(inventDimCombination.ProductLifecycleStateId);
        releaseManager.release();

        // re-intialize inventDimCombination buffer
        inventDimCombinationReleased = InventDimCombination::findByDistinctProductVariant(ecoResDistinctProductVariant.RecId);
        inventDimCombination.data(inventDimCombinationReleased);

        ttscommit;

        if (isFallbackFeatureAndParameterEnabled)
        {
            ecoResProductTranslationFallbackView = EcoResProductTranslationFallbackView::findFallbackTranslationByLanguage(ecoResDistinctProductVariant.RecId, EcoResProductParametersFallbackLanguage::getSelectedFallbackLanguage());

            EcoResProductTranslationFallbackName.allowEdit(false);
            EcoResProductTranslationFallbackDescription.allowEdit(false);
        }
    }
    else
    {
        InventDimCombination.update();            
    }

    inventDimCombination_ds.setCurrent();
}

