public void setNameAndProductNumberFromNomenclature()
{
    if(InventDistinctProductValidator::construct().isDistinctProductVariantFullySpecifiedByInventDim(EcoResDistinctProductVariant.ProductMaster, this.createInventDimFromDatasources()))
    {
        EcoResProductMaster currProductMaster  = element.productVariantsFormHelper().parmEcoResProductMaster();

        container dimensions =  element.productVariantsFormHelper().getProductDimensionValues();

        ecoResDistinctProductVariant.DisplayProductNumber = EcoResProductNumberBuilderVariant::buildFromProductMasterAndDimensions(
                currProductMaster,
                dimensions);

        if(ecoResProductTranslation.Name == '' && ecoResDistinctProductVariant.SearchName == '')
        {
            ecoResProductTranslation.Name = EcoResProductNumberBuilderVariant::buildNameFromProductMasterAndDimensions(
                currProductMaster,
                dimensions);

            ecoResDistinctProductVariant.SearchName = '';
            ecoResDistinctProductVariant.setSearchName(ecoResProductTranslation.Name);
        }
    }
}

