}

public void init()
{
    EcoResProduct           callerProductRecord;
    Common                  callerRecord = element.args().record();
    QueryBuildDataSource    qbds;
    QueryBuildRange         itemIdRange;

    super();

    if (element.productVariantsFormHelper().parmIsOpenedForSingleProduct())
    {
        this.object(fieldNum(InventDimCombination, ItemId)).visible(false);

        //if we are called from Released products list page then the form is filtered to the correct product
        //thanks to a dynalink between InventDimCombination and calling InventTable record
        //However when called with product record we must do some manual work
        if (callerRecord is EcoResProduct)
        {
            callerProductRecord = callerRecord as EcoResProduct;
            qbds = this.query().dataSourceName(this.name());
            itemIdRange = qbds.addRange(fieldNum(InventDimCombination, ItemId));
            itemIdRange.isRangeStringLiteral(true);
            itemIdRange.value(InventTable::findByProduct(callerProductRecord.RecId).ItemId);                    
            itemIdRange.status(RangeStatus::Hidden);
        }
    }
    else
    {
        this.allowCreate(false);
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        if (!isEcoResProductVariantDisplaySortingDisabled)
        {
            this.queryBuildDataSource().addOrderByField(fieldNum(InventDimCombination, ItemId));
        }
    }
}

public void selectionChanged()
{
    super();

    element.productVariantsFormHelper().handleFormStateForVariant(ecoResDistinctProductVariant.RecId);

    if (MultiSelectionHelper::currentRecordOnlySelected(this))
    {
        UnitOfMeasureConversion.Enabled(EcoResProductUnitConverter::isProductUnitConversionPerVariantEnabled() && EcoResProductmaster::find(ecoResDistinctProductVariant.ProductMaster).IsProductVariantUnitConversionEnabled);
    }
    else
    {
        // Multiselect not support for UoM
        UnitOfMeasureConversion.enabled(false);
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        RetailKitConfigurations.visible(EcoResDistinctProductVariant.isRetailKitConfiguration());
    }
}

