public void modified()
{
    EcoResProductMasterColor ecoResProductMasterColor;

    element.setNameAndProductNumberFromNomenclature();

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        select RetailWeight, RetailDisplayOrder from ecoResProductMasterColor
            where ecoResProductMasterColor.Color == ecoResProductVariantColor.Color &&
                  ecoResProductMasterColor.ColorProductMaster == ecoResDistinctProductVariant.ProductMaster;

        ecoResProductVariantColor.RetailWeight = ecoResProductMasterColor.RetailWeight;
        ecoResProductVariantColor.RetailDisplayOrder = ecoResProductMasterColor.RetailDisplayOrder;
    }
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    EcoResColorName colorName;

    // Don't call super.

    colorName = _formReferenceControl.filterValue(AbsoluteFieldBinding::construct(fieldStr(EcoResColor, Name), tableStr(EcoResColor))).value();

    return EcoResColor::resolveProductVariantColor(colorName,
        element.productVariantsFormHelper().parmEcoResProductMaster().RecId,
        ecoResProductVariantColor.ProductDimensionAttribute);
}

public void jumpRef()
