public void write()
{
    boolean mustUpdateBuffer;

    if (!element.productVariantsFormHelper().isNewProductVariant())
    {
        mustUpdateBuffer = !ecoResProductTranslation.canMaintainTranslation();

        super();

        // after the record will be deleted in the update table method, the data source should be reread
        if (mustUpdateBuffer)
        {
            ecoResProductTranslation.reread();
        }
    }
}

public void init()
{
    super();

    EcoResProductTranslation::queryAddDefaultLanguage(this.query(), this.name());
}

public boolean validateWrite()
{
    boolean     ret = true;

    if (!element.productVariantsFormHelper().isNewProductVariant())
    {
        if (!ecoResProductTranslation.Product || !ecoResProductTranslation.LanguageId)
        {
            ecoResProductTranslation.Product = ecoResDistinctProductVariant.RecId;        //Initialize in order to avoid validateWrite to fail.
            ecoResProductTranslation.setDefaultLanguage();                               //Initialize in order to avoid validateWrite to fail.
        }

        if (ecoResProductTranslation.canMaintainTranslation())
        {
            ret = super();
        }
    }

    return ret;
}

public void initValue()
{
    ecoResProductTranslation.setDefaultLanguage();

    super();
}

public void modified()
{
    super();

    if (ecoResProductTranslation.Name)
    {
        ecoResDistinctProductVariant.setSearchName(ecoResProductTranslation.Name);
    }
}

public int active()
{
    boolean isNewProductVariant = element.productVariantsFormHelper().isNewProductVariant();
    EcoResProductTranslationFallbackName.allowEdit(isNewProductVariant);
    EcoResProductTranslationFallbackDescription.allowEdit(isNewProductVariant);

    return super();
}

public int active()
{
    int ret;

    ret = super();

    element.setButtonsEnabled();

    return ret;
}

public void init()
{
    if (!isConfigurationkeyEnabled(configurationKeyNum(Retail)) || !useEcoResProductVariantDisplayOrderView)
    {
        this.linkType(FormLinkType::Passive);
    }

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)) && useEcoResProductVariantDisplayOrderView)
    {
        this.queryBuildDataSource().addLink(fieldNum(EcoResProductVariantSize, DistinctProductVariant), fieldNum(EcoResProductVariantSizeDisplayOrderView, DistinctProductVariant));
        this.queryBuildDataSource().addLink(fieldNum(EcoResProductVariantSize, Size), fieldNum(EcoResProductVariantSizeDisplayOrderView, Size));

        if (!isEcoResProductVariantDisplaySortingDisabled)
        {
            this.queryBuildDataSource().addOrderByField(fieldNum(EcoResProductVariantSizeDisplayOrderView, RetailDisplayOrder));
            this.queryBuildDataSource().addOrderByField(fieldNum(EcoResProductVariantSizeDisplayOrderView, Name));
        }
    }
}

