        this.queryBuildDataSource().addLink(fieldNum(EcoResProductVariantColor, Color), fieldNum(EcoResProductVariantColorDisplayOrderView, Color));

        if (!isEcoResProductVariantDisplaySortingDisabled)
        {
            this.queryBuildDataSource().addOrderByField(fieldNum(EcoResProductVariantColorDisplayOrderView, RetailDisplayOrder));
            this.queryBuildDataSource().addOrderByField(fieldNum(EcoResProductVariantColorDisplayOrderView, Name));
        }
    }
