EmplAdvTable_RU advanceTable()
{
    return emplAdvTable_RU;
}

void callerRefresh()
{
    EmplAdvLine_RU  emplAdvLine;
    TmpTaxWorkTrans tmpTaxWorkTrans;        

    ttsbegin;

    tmpTaxWorkTrans.setTmpData(taxSource.tmpTaxWorkTrans());

    while select tmpTaxWorkTrans
    {
        emplAdvLine = EmplAdvLine_RU::findRecId(tmpTaxWorkTrans.HeadingRecId, true);
        if (emplAdvLine)
        {
            emplAdvLine.calcTotal();
            emplAdvLine.doUpdate();
        }
    }

    ttscommit;

    emplAdvLine_RU_DS.reread();
    emplAdvLine_RU_DS.refresh();
    emplAdvTable_RU_ds.reread();
    emplAdvTable_RU_ds.refresh();
}

/// <summary>
/// Contains the logic for enabling/disabling of buttons related to Advance report line.
/// </summary>
/// <remarks>
/// Is called when current Advance report line changes.
/// </remarks>
public void enableLineButtons()
{
    // only enable budget checks results inquiry when a budget check was performed on the line
    budgetCheckResultsInquiryLine.enabled(BudgetSourceSourceDocumentLine::existBudgetSource(emplAdvLine_RU.SourceDocumentLine));
    // only enable budget check button when the line has SourceDocumentLine field filled
    checkBudget.enabled(emplAdvLine_RU.SourceDocumentLine != 0);
}

void gotoLines()
{
    lineSpec.setFocus();
}

public void  init()
{
    Object      caller;
    DictClass   dict;

    if (element.args().caller())
    {
        caller = element.args().caller();
        dict   = new DictClass(classIdGet(caller));

        while (dict && ! factureSource && dict.id() != classNum(Object))
        {
            if (dict.id() == classNum(FactureSource_RU))
            {
                factureSource = element.args().caller();
            }
            else
            {
                dict          = new DictClass(dict.extend());
            }
        }
    }

    super();

    LineSpec_LedgerAccount.parmPostingType(LedgerPostingType::None);
    GroupLedger_LedgerAccount.parmPostingType(LedgerPostingType::None);
    LineSpec_LedgerAccount.parmValidateBlockedForManualEntry(true);
    GroupLedger_LedgerAccount.parmValidateBlockedForManualEntry(true);

    emplAdvTableForm = new EmplAdvTableForm_RU();

    deleting = false;

    isBudgetControlEnabled = EmplAdvTable_RU::isBudgetControlEnabled();
    budgetCheckResult.visible(isBudgetControlEnabled);
}

public void run()
{
    element.setBudgetButtonsVisibility();

    super();

    if (element.args().menuItemName() == menuitemDisplayStr(EmplAdvTable_ForNewAction))
    {
        identification_TransDate.setFocus();
    }
}

private void setBudgetButtonsVisibility()
{
    budgetControl.visible(isBudgetControlEnabled);
    budgetCheckResultsInquiryLine.visible(isBudgetControlEnabled);
    checkBudget.visible(isBudgetControlEnabled);
}

void setCaptionText()
{
    element.design().caption(strFmt("@SYS76498", "@GLS101905", emplAdvTable_RU.AdvanceId));
}

void setDimensionsAllowEdit()
{       
    LineDimensionEntryControl.allowEdit(emplAdvLine_RU_ds.object(fieldNum(EmplAdvLine_RU, LedgerDimension)).allowEdit() ? NoYesUnchanged::Yes : NoYesUnchanged::No);

    if (emplAdvTable_RU.Status == EmplAdvStatus_RU::Posted)
    {            
        LineDimensionEntryControl.allowEdit(NoYesUnchanged::No);
    }
}

void setHeaderDimensionsAllowEdit()
{
    if (emplAdvTable_RU.Status == EmplAdvStatus_RU::Posted)
    {            
        HeaderDimensionEntryControl.allowEdit(NoYesUnchanged::No);
    }
}

public void setTitleField()
{
    headerTitle.text(strFmt('%1 : %2 - %3', emplAdvTable_RU.AdvanceId,
                                            emplAdvTable_RU.EmplAccount,
                                            EmployeeTable_RU::findByEmployeeId(emplAdvTable_RU.EmplAccount).employeeName()));
}

void showAndEdit()
{
    emplAdvLine_RU_ds.allowCreate (emplAdvTable_RU.RecId && emplAdvTable_RU.Status == EmplAdvStatus_RU::Open);
    emplAdvLine_RU_ds.allowEdit   (emplAdvTable_RU.RecId && emplAdvTable_RU.Status == EmplAdvStatus_RU::Open);
    emplAdvLine_RU_ds.allowDelete (emplAdvTable_RU.RecId && emplAdvTable_RU.Status == EmplAdvStatus_RU::Open);

    emplAdvTable_RU_ds.allowEdit  (emplAdvTable_RU.Status == EmplAdvStatus_RU::Open);
    emplAdvTable_RU_ds.allowDelete(emplAdvTable_RU.Status == EmplAdvStatus_RU::Open);
    officialsTrans_RU_DS.allowEdit(emplAdvTable_RU.Status == EmplAdvStatus_RU::Open);

    subLedgerJournalHeader.enabled(
        accountingDistributionsDocumentViewHdr.enabled(emplAdvTable_RU.hasSourceDocuments()));
}

void showTax(EmplAdvLine_RU _emplAdvLine)
{
    Args    args;
    FormRun formRun;

    if (emplAdvTable_RU.Status != EmplAdvStatus_RU::Posted)
    {
        taxSource = TaxEmplAdvance_RU::calcAndFillWorkTrans(emplAdvTable_RU, _emplAdvLine);

        Tax::showTaxes(menuitemDisplayStr(TaxTmpWorkEmplAdvance), taxSource, emplAdvLine_RU, element);
    }
    else
    {
        args = new Args();
        args.caller(this);
        args.record(emplAdvTable_RU);

        formRun = new MenuFunction(menuitemDisplayStr(TaxTrans), MenuItemType::Display).create(args);
        formRun.run();
    }
}

public int active()
{
    int ret = super();

    element.setTitleField();

    element.showAndEdit();

    element.setHeaderDimensionsAllowEdit();

    return ret;
}

