{
    EmplAdvanceId_RU    oldAdvanceId = emplAdvTable_RU.AdvanceId;

    super();

    emplAdvTableForm.allocateNumSeqCode(true, oldAdvanceId);
}

public void init()
{
    super();

    if (isBudgetControlEnabled)
    {
        this.cacheAddMethod(tableMethodStr(EmplAdvTable_RU, displayBudgetCheckResult), false);
    }

    if (factureSource)
    {
        this.query().dataSourceTable(tableNum(EmplAdvTable_RU)).addRange(fieldNum(EmplAdvTable_RU, RecId)).value(factureSource.jourRange());
    }
}

