public int active()
{
    int ret = super();

    element.enableLineButtons();

    advLineType = EmplAdvanceLineType_RU::construct(emplAdvLine_RU);
    advLineType.setEdit(this);

    element.setDimensionsAllowEdit();

    this.enableFixedExchRate();

    accountingDistribution.enabled(subledgerJournal.enabled(emplAdvLine_RU.SourceDocumentLine ? true : false));
    return ret;
}

void delete()
{
    ttsbegin;

    if (! advLineType.delete(this))
    {
        TaxWorkRegulation::clearRegulation(emplAdvLine_RU.TableId,emplAdvLine_RU.RecId);
        super();
    }

    ttscommit;

    emplAdvTable_RU_ds.reread();
}

void  enableFixedExchRate()
{
    currency.visible(advLineType.showCurrencyGroup());

    if (advLineType.showCurrencyGroup())
    {
        currency_Triangulation.allowEdit(advLineType.triangulation(emplAdvTable_RU, emplAdvLine_RU));
        currency_M_editExchRate.allowEdit(advLineType.exchRate(emplAdvTable_RU, emplAdvLine_RU));
        currency_M_editExchRateSecond.allowEdit(advLineType.exchRateSecond(emplAdvTable_RU, emplAdvLine_RU));

        currency_Triangulation.skip(!currency_Triangulation.allowEdit());
        currency_M_editExchRate.skip(!currency_M_editExchRate.allowEdit());
        currency_M_editExchRateSecond.skip(!currency_M_editExchRateSecond.allowEdit());
    }
}

public void executeQuery()
{
    super();

    if (findRecord)
    {
        emplAdvLine_RU_ds.positionToRecord(findRecord);
        findRecord = null;
    }
}

public void init()
{
    super();

    if (isBudgetControlEnabled)
    {
        this.cacheAddMethod(tableMethodStr(EmplAdvLine_RU, displayBudgetCheckResult));
    }

    if (factureSource)
    {
        this.query().dataSourceTable(tableNum(EmplAdvLine_RU)).addRange(fieldNum(EmplAdvLine_RU, RecId)).value(factureSource.transRange());
    }
}

public void initValue()
{
    super();

    emplAdvLine_RU.LedgerDimension  = HcmWorkerGroup_RU::findByEmployeeId(emplAdvTable_RU.EmplAccount).OffsetLedgerDimension;
    emplAdvline_RU.DefaultDimension = emplAdvTable_RU.DefaultDimension;
    emplAdvLine_RU.DocumentDate     = emplAdvTable_RU.TransDate;
    emplAdvLine_RU.CurrencyCode     = CompanyInfoHelper::standardCurrency();
    emplAdvLine_RU.PurchBookVATProcessParameters_RU = PurchBookVATProcessParameters_RU::defaultParms().RecId;
    emplAdvLine_RU_ds.ledgerDimensionChange();

    emplAdvLine_RU.initExchRates();
}

void ledgerDimensionChange()
{
    MainAccount mainAccount = LedgerDimensionFacade::getMainAccountFromLedgerDimension(emplAdvLine_RU.LedgerDimension);

    if (emplAdvLine_RU.LedgerDimension && ! emplAdvLine_RU.EmplAdvExpendTable_RU)
    {
        emplAdvLine_RU.TaxGroup     = MainAccountLegalEntity::findByMainAccountLegalEntity(mainAccount.RecId, CompanyInfo::current()).TaxGroup;
        emplAdvLine_RU.TaxItemGroup = MainAccountLegalEntity::findByMainAccountLegalEntity(mainAccount.RecId, CompanyInfo::current()).TaxItemGroup;
    }
}

