{
    emplAdvLine_RU.setTouched();
}

/// <summary>
/// Updates budget check result display cache.
/// </summary>
public void updateBudgetCheckResultDisplay()
{
    if (this.getFirst(1))
    {
        this.research(true);
    }
    else
    {
        this.cacheCalculateMethod(tableMethodStr(EmplAdvLine_RU, displayBudgetCheckResult));
        this.refresh();
    }
}

public void write()
{
    emplAdvLine_RU.AmountCur         = emplAdvLine_RU.AmountCur ?
                                       emplAdvLine_RU.AmountCur :
                                       emplAdvLine_RU.AmountCurApproved;

    emplAdvLine_RU.AmountCurApproved = emplAdvLine_RU.AmountCurApproved ?
                                       emplAdvLine_RU.AmountCurApproved :
                                       emplAdvLine_RU.AmountCur;

    super();

    emplAdvTable_RU_ds.reread();
    emplAdvTable_RU_ds.refresh();
}

public void modified()
{
    super();

    emplAdvLine_RU.initExchRates();

    emplAdvLine_RU_ds.enableFixedExchRate();

    emplAdvLine_RU_ds.refresh();
}

