public int active()
{
    int             ret;
    RPayHistory     rPayHistory;

    ret = super();

    if (ret)
    {
        HcmWorker worker = EmployeeTable_RU::findByEmployeeId(EmployeeTable_RU.EmployeeId).worker();
        OMOperatingUnit department = HcmWorkerHelper::getPrimaryDepartment(worker.RecId, emplAdvTable_RU.TransDate);

        title = HcmWorkerHelper::getWorkerPrimaryPositionTitle(worker.RecId, emplAdvTable_RU.TransDate);
        organizationId = department.OMOperatingUnitNumber;
        orgName = department.Name;
    }

    return ret;
}

void clicked()
{
    if (emplAdvTable_RU)
    {
        // Submit to source document processor for all lines for an on-demand GL budget check
        emplAdvTable_RU.checkBudgetForAllLines();

        emplAdvTable_RU_ds.updateBudgetCheckResultDisplay();
    }
}

void clicked()
{
    if (! emplAdvLine_RU.RecId)
    {
        throw error(Error::missingRecord(tablePName(EmplAdvLine_RU)));
    }
    if (emplAdvTable_RU.hasSourceDocuments())
    {
        super();
    }
}

void clicked()
{
    SourceDocumentLineList lines = emplAdvTable_RU.sourceDocumentLineList();
    Args args = new Args();
    if (! lines.empty())
    {
        args.parmObject(lines);
        args.record(emplAdvTable_RU.findFirstDocumentImplementation());
        this.menufunction().run(args);
    }
}

