Method.Source:
[Form]
    public class EmplOpenTransReverse_RU extends FormRun
    {
        Set                  emplSettlementSet;
        const int Value3 =3;	    
}

void buildQuery(FormDataSource    _formDataSource)
{
    QueryBuildRange         qbr;
    QueryBuildDataSource    qbds;
    boolean                 isDebit;

    qbds    = _formDataSource.query().dataSourceNo(1);
    isDebit = qbds.name() == emplTransDebit_ds.name();

    qbr = findOrCreateRange_W(qbds, fieldNum(EmplTrans_RU, Reversed));
    qbr.value(queryValue(NoYes::No));
    qbr.status(RangeStatus::Locked);

    qbr = findOrCreateRange_W(qbds, fieldNum(EmplTrans_RU, AmountCur));
    qbr.value(isDebit ? "@GLS111253" : "@GLS111254");
    qbr.status(RangeStatus::Locked);

    qbr = findOrCreateRange_W(qbds, fieldNum(EmplTrans_RU, TransType));
    qbr.value(SysQuery::valueNot(EmplTransSourceType_RU::ExchAdj));
    qbr.status(RangeStatus::Locked);

    qbds = _formDataSource.query().dataSourceTable(tableNum(EmplSettlement_RU));

    qbds.clearLinks();
    qbds.clearDynalinks();

    qbds.addDynalink(isDebit ?
                     fieldNum(EmplSettlement_RU, DebitRecId) :
                     fieldNum(EmplSettlement_RU, CreditRecId),
                     isDebit ? emplTransDebit : emplTransCredit,
                     fieldNum(EmplTrans_RU, RecId));

    qbds = _formDataSource.query().dataSourceNo(Value3);

    qbds.clearLinks();
    qbds.clearDynalinks();

    qbds.addDynalink(fieldNum(EmplTrans_RU, RecId),
                     isDebit ?
                     emplSettlementDebit : emplSettlementCredit,
                     isDebit ?
                     fieldNum(EmplSettlement_RU, CreditRecId) :
                     fieldNum(EmplSettlement_RU, DebitRecId));

    qbr = findOrCreateRange_W(qbds, fieldNum(EmplTrans_RU, TransType));
    qbr.value(SysQuery::valueNot(EmplTransSourceType_RU::ExchAdj));
}

public void init()
{	
    super();

    if (!element.args().record())
    {
        throw error(Error::missingRecord(this.name()));
    }

    emplSettlementSet    = new Set(typeName2Type(extendedTypeStr(RecId)));

    this.buildQuery(emplTransDebit_ds);
    this.buildQuery(emplTransCredit_ds);
}

NoYes updateSet(boolean _set, EmplSettlement_RU _emplSettlement, boolean _mark)
{
    EmplSettlement_RU   emplSettlementLocal;
    EmplTrans_RU        emplTransLocal;

    void processExchAdjustment()
    {
        select firstonly RecId from emplSettlementLocal
            exists join emplTransLocal
                where emplTransLocal.TransType  == EmplTransSourceType_RU::ExchAdj &&
                      emplTransLocal.RefRecId   == _emplSettlement.RecId           &&
                      emplTransLocal.RefTableId == _emplSettlement.TableId         &&
                      emplTransLocal.Reversed   == NoYes::No                       &&
                      (emplTransLocal.RecId     == emplSettlementLocal.CreditRecId ||
                      emplTransLocal.RecId      == emplSettlementLocal.DebitRecId);
        if (_mark)
        {
            emplSettlementSet.add(emplSettlementLocal.RecId);
        }
        else
        {
            emplSettlementSet.remove(emplSettlementLocal.RecId);
        }
    }        

    if (! _set)
    {
        return emplSettlementSet.in(_emplSettlement.RecId);
    }

    if (_mark)
    {
        emplSettlementSet.add(_emplSettlement.RecId);
    }
    else
    {
        emplSettlementSet.remove(_emplSettlement.RecId);
    }

    processExchAdjustment();

    emplTransDebit_ds.research();
    emplTransCredit_ds.research();

    reverseNow.enabled(emplSettlementSet.elements());

    return _mark;
}

/// <summary>
/// Mark the transaction in the Debit.
/// </summary>
/// <param name = "_set">Check true or false.</param>
/// <param name = "_emplSettlement">Get the record in EmplSettlement.</param>
/// <param name = "_mark">Check mark the transaction or not.</param>
/// <returns>Return the value.</returns>
edit NoYes markTransDebit(boolean _set, EmplSettlement_RU _emplSettlement, NoYes _mark)
{
    return element.updateSet(_set, _emplSettlement, _mark);
}

/// <summary>
/// Mark the transaction in the Credit.
/// </summary>
/// <param name = "_set">Check true or false.</param>
/// <param name = "_emplSettlement">Get the record in EmplSettlement.</param>
/// <param name = "_mark">Check mark the transaction or not.</param>
/// <returns>Return the value.</returns>
edit NoYes markTransCredit(boolean _set, EmplSettlement_RU _emplSettlement, NoYes _mark)
{
    return element.updateSet(_set, _emplSettlement, _mark);
}

void clicked()
{
    EmplSettleReverseManual_RU    emplSettleReverseManual;
    boolean                       reverseOK = true;

    super();

    emplSettleReverseManual = new EmplSettleReverseManual_RU(dateNull());

    emplSettleReverseManual.parmSettlementSet(emplSettlementSet);

    try
    {
        emplSettleReverseManual.reverseNow(emplTransDebit.EmplAccount);
    }
    catch(Exception::Error)
    {
        reverseOK = false;
    }

    if (reverseOK)
    {
        emplSettlementSet = new Set(typeName2Type(extendedTypeStr(RecId)));
        reverseNow.enabled(false);
    }

    emplTransDebit_ds.executeQuery();
    emplTransCredit_ds.executeQuery();
}
