Method.Source:
    [Form]
    public class EmplOpenTrans_RU extends FormRun
    {
        Set                  transDebitSet, transCreditSet;
        [FormObservable]
        AmountMST            remainAmountMST;
}

void buildQuery(FormDataSource    _formDataSource)
{
    QueryBuildRange         qbr;
    QueryBuildDataSource    qbds;

    qbds = _formDataSource.query().dataSourceTable(tableNum(EmplTrans_RU));

    qbr = findOrCreateRange_W(qbds, fieldNum(EmplTrans_RU, Closed));
    qbr.status(RangeStatus::Locked);
    qbr.value(queryValue(dateNull()));

    qbr = findOrCreateRange_W(qbds, fieldNum(EmplTrans_RU, Reversed));
    qbr.status(RangeStatus::Locked);
    qbr.value(queryValue(NoYes::No));

    qbr = findOrCreateRange_W(qbds, fieldNum(EmplTrans_RU, AmountCur));
    qbr.status(RangeStatus::Locked);
    qbr.value(qbds.name() == emplTransDebit_ds.name() ? "@GLS111253" : "@GLS111254");
}

public void init()
{
    super();

    if (!element.args().record())
    {
        throw error(Error::missingRecord(this.name()));
    }

    transDebitSet        = new Set(typeName2Type(extendedTypeStr(RecId)));
    transCreditSet       = new Set(typeName2Type(extendedTypeStr(RecId)));
}

/// <summary>
/// Display Balance value.
/// </summary>
/// <returns>Return the Balance value.</returns>
display AmountMST remainAmountMST()
{
    return remainAmountMST;
}

NoYes updateSet(boolean _set, FormDataSource _formDataSource, EmplTrans_RU _emplTrans, boolean _mark)
{
    Set    transSet;

    transSet = _formDataSource.name() == emplTransDebit_ds.name() ?
                transDebitSet : transCreditSet;

    if (! _set)
    {
        return transSet.in(_emplTrans.RecId);
    }

    if (_mark)
    {
        transSet.add(_emplTrans.RecId);
        remainAmountMST += _emplTrans.remainAmountMST();
    }
    else
    {
        transSet.remove(_emplTrans.RecId);
        remainAmountMST -= _emplTrans.remainAmountMST();
    }

    updateNow.enabled(transDebitSet.elements() && transCreditSet.elements());

    _formDataSource.refresh();

    return _mark;
}

/// <summary>
/// Mark the transaction in the Debit.
/// </summary>
/// <param name = "_set">Set or not.</param>
/// <param name = "_emplTrans">Get the record in EmplTrans_RU.</param>
/// <param name = "_mark">Check mark the transaction or not.</param>
/// <returns>Return the value.</returns>
edit NoYes markTransDebit(boolean _set, EmplTrans_RU _emplTrans, NoYes _mark)
{
    return element.updateSet(_set, this, _emplTrans, _mark);
}

/// <summary>
/// Mark the transaction in the Credit.
/// </summary>
/// <param name = "_set">Set or not.</param>
/// <param name = "_emplTrans">Get the record in EmplTrans_RU.</param>
/// <param name = "_mark">Check mark the transaction or not.</param>
/// <returns>Return the value.</returns>
edit NoYes markTransCredit(boolean _set, EmplTrans_RU _emplTrans, NoYes _mark)
{
    return element.updateSet(_set, this, _emplTrans, _mark);
}

void clicked()
{
    EmplSettleManual_RU    emplSettleManual;
    boolean                settleOK = true;

    super();

    emplSettleManual = new EmplSettleManual_RU(dateMax());

    emplSettleManual.parmDebitSet(transDebitSet);
    emplSettleManual.parmCreditSet(transCreditSet);
    emplSettleManual.parmProfileSettlement(false);

    try
    {
        emplSettleManual.settleNow(emplTransDebit.EmplAccount);
    }
    catch(Exception::Error)
    {
        settleOK = false;
    }

    if (settleOK)
    {
        remainAmountMST      = 0;
        transDebitSet        = new Set(typeName2Type(extendedTypeStr(RecId)));
        transCreditSet       = new Set(typeName2Type(extendedTypeStr(RecId)));
        updateNow.enabled(false);
    }

    emplTransDebit_ds.executeQuery();
    emplTransCredit_ds.executeQuery();
}
