Method.Source:
    [Form]
    public class EmplSettlement_RU extends FormRun
    {
        EmplTrans_RU        callerEmplTrans;

        QueryBuildRange     qbrEmplAccount,
                            qbrDebitRecId,
                            qbrCreditRecId;

        [FormObservable]
        AmountCur           settledAmountCur;
        [FormObservable]
        AmountCur           settledAmountCurOff;
        [FormObservable]
        AmountMST           settledAmountMst;
        [FormObservable]
        AmountMST           settledAmountMSTExchAdj;

        EmployeeTable_RU    callerEmployeeTable;
}

public void init()
{
    super();

    if (element.args() && element.args().dataset())
    {
        switch (element.args().dataset())
        {
            case tableNum(EmplTrans_RU):
                callerEmplTrans = element.args().record();
                employee.visible(false);
                element.initSelector();
                showTransMode.enabled(false);
                break;
            case tableNum(EmployeeTable_RU):
                callerEmployeeTable = element.args().record();
                employee.visible(false);
                emplTransDebit_EmplAccount.visible(false);
                break;
        }
    }

    element.rebuildQuery();
}

void rebuildQuery()
{
    QueryBuildDataSource    debitQbds,  settlementQbds;
    QueryBuildRange         qbr;
    boolean                 debit = showTransMode.selection() == EmplSettlementSide_RU::Debit;

    debitQbds  = emplTransDebit_q.dataSourceTable(tableNum(EmplTrans_RU));

    debitQbds.clearLinks();
    debitQbds.clearDynalinks();

    settlementQbds  = emplSettlementView_q.dataSourceTable(tableNum(EmplSettlement_RU));

    settlementQbds.clearLinks();
    settlementQbds.clearDynalinks();

    settlementQbds.addDynalink(!debit ?
                               fieldNum(EmplSettlement_RU, CreditRecId) :
                               fieldNum(EmplSettlement_RU, DebitRecId),
                               emplTransDebit,
                               fieldNum(EmplTrans_RU, RecId));

    if (callerEmplTrans)
    {
        findOrCreateRange_W(debitQbds, fieldNum(EmplTrans_RU, RecId)).value(queryValue(callerEmplTrans.RecId));
    }
    else if (!callerEmplTrans && !callerEmployeeTable && !employee.text())
    {
        findOrCreateRange_W(debitQbds, fieldNum(EmplTrans_RU, RecId)).value(queryValue(0));
    }

    qbrEmplAccount = findOrCreateRange_W(debitQbds, fieldNum(EmplTrans_RU, EmplAccount));
    qbrEmplAccount.status(RangeStatus::Hidden);

    if (callerEmployeeTable)
    {
        qbrEmplAccount.value(queryValue(callerEmployeeTable.EmployeeId));
    }

    qbr = findOrCreateRange_W(debitQbds, fieldNum(EmplTrans_RU, AmountCur));
    qbr.status(RangeStatus::Hidden);
    qbr.value(debit ?  "@GLS111253" : "@GLS111254");

    qbr = findOrCreateRange_W(debitQbds, fieldNum(EmplTrans_RU, TransType));
    qbr.value(strFmt('((%1 != %2) && (%1 != %3))',
                     fieldStr(EmplTrans_RU, TransType),
                     int2str(EmplTransSourceType_RU::ExchAdj),
                     int2str(EmplTransSourceType_RU::CrossAccountSettle)));
    qbr.status(RangeStatus::Hidden);

    qbr = findOrCreateRange_W(debitQbds, fieldNum(EmplTrans_RU, Reversed));
    qbr.value(queryValue(NoYes::No));
    qbr.status(RangeStatus::Hidden);
}

/// <summary>
/// This method shows/hides forms bottom part.
