private void visibleTab()
{
    CtrlSplitVertical.visible(Overview.isExpanded());
    ChildTab.visible(Overview.isExpanded());
}

public void linkActive()
{
    element.initSelector();
    element.rebuildQuery();

    super();
}

public void init()
{
    QueryBuildDataSource    qbds;

    super();

    qbds = emplTransCredit_q.dataSourceTable(tableNum(EmplTrans_RU));

    qbds.clearLinks();
    qbds.clearDynalinks();

    qbds = qbds.addDataSource(tableNum(EmplSettlement_RU));

    qbds.joinMode(JoinMode::ExistsJoin);

    qbrDebitRecId = qbds.addRange(fieldNum(EmplSettlement_RU, DebitRecId));
    qbrDebitRecId.status(RangeStatus::Hidden);

    qbrCreditRecId = qbds.addRange(fieldNum(EmplSettlement_RU, CreditRecId));
    qbrCreditRecId.status(RangeStatus::Hidden);
}

public void executeQuery()
{
    boolean                 debit;
    QueryBuildDataSource    qbds;
    EmplSettlement_RU       settlement;
    str                     range;
    QueryBuildRange         qbr;

    debit = showTransMode.selection() == EmplSettlementSide_RU::Debit;

    qbds = emplTransCredit_q.dataSourceTable(tableNum(EmplSettlement_RU));

    qbds.clearLinks();
    qbds.clearDynalinks();

    qbrDebitRecId.value(SysQuery::valueUnlimited());
    qbrCreditRecId.value(SysQuery::valueUnlimited());

    qbds.addLink(fieldNum(EmplTrans_RU, RecId),
                 debit ?
                 fieldNum(EmplSettlement_RU, CreditRecId) :
                 fieldNum(EmplSettlement_RU, DebitRecId));

    for (settlement = emplSettlementView_ds.getFirst();
         settlement;
         settlement = emplSettlementView_ds.getNext())
    {
        range = queryRangeConcat(range, debit ? settlement.CreditRecId : settlement.DebitRecId);
    }

    if (! range)
    {
        qbrDebitRecId.value(queryValue(0));
        qbrCreditRecId.value(queryValue(0));
    }
    else
    {
        if (debit)
        {
            qbrCreditRecId.value(range);
            qbrDebitRecId.value(SysQuery::valueUnlimited());
        }
        else
        {
            qbrDebitRecId.value(range);
            qbrCreditRecId.value(SysQuery::valueUnlimited());
        }
    }

    qbds = emplTransCredit_q.dataSourceTable(tableNum(EmplTrans_RU));
    qbr = findOrCreateRange_W(qbds, fieldNum(EmplTrans_RU, TransType));
    qbr.value(SysQuery::valueNot(EmplTransSourceType_RU::ExchAdj));
    qbr.status(RangeStatus::Hidden);

    super();
}

display AmountMST amountMST(EmplTrans_RU _transTable)
{
    EmplSettlement_RU settleTable;

    settledAmountCur    = 0;
    settledAmountMst    = 0;
    settledAmountCurOff = 0;

    if (showTransMode.selection() == EmplSettlementSide_RU::Credit)
    {
        select firstonly SettleAmountCurDebit, SettleAmountCurCredit,
                         SettleAmountMST, ExchAdjustment from settleTable
            where settleTable.CreditRecId == emplTransDebit.RecId &&
                  settleTable.DebitRecId  ==_transTable.RecId;

         settledAmountCurOff    = settleTable.SettleAmountCurDebit;
         settledAmountCur       = - settleTable.SettleAmountCurCredit;
    }
    else
    {
        select firstonly SettleAmountCurDebit, SettleAmountCurCredit,
                         SettleAmountMST, ExchAdjustment from settleTable
            where settleTable.CreditRecId == _transTable.RecId &&
                  settleTable.DebitRecId  == emplTransDebit.RecId;

         settledAmountCur    = settleTable.SettleAmountCurDebit;
         settledAmountCurOff = - settleTable.SettleAmountCurCredit;
    }

    settledAmountMst    = settleTable.SettleAmountMST;
    settledAmountMSTExchAdj = settleTable.ExchAdjustment;

    return settledAmountMst;
}

