/// Method called when user select employee.
/// </summary>
/// <returns></returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        emplTransDebit_q.dataSourceTable(tableNum(EmplTrans_RU)).clearRange(fieldNum(EmplTrans_RU, RecId));
        qbrEmplAccount.value(queryValue(this.text()));
        emplTransDebit_ds.executeQuery();
    }

    return ret;
}

