[Form]
public class EmplTrans_RU extends FormRun
{
    NoYes               showAll;
    EmplTransSource_RU  transSource;
    QueryBuildRange     criteriaOpen;

}

public void researchSourceTable()
{
    TmpFrmVirtual   buffer;

    transSource = EmplTransSource_RU::newEmplTrans(emplTrans);
    if (transSource)
    {
        buffer = transSource.tmpFrmVirtual();
        tmpFrmVirtual.setTmpData(buffer);
        tmpFrmVirtual_ds.research();
    }
}

public void displayOption(Common _emplTrans, FormRowDisplayOption _options)
        {
            EmplTrans_RU tmpEmplTrans = _emplTrans;
            if (tmpEmplTrans.Reversed)
            {
                const RGBint reversedTransColor = 180;
                _options.backColor(WinAPI::RGB2int(reversedTransColor,reversedTransColor,reversedTransColor));
            }

            super(_emplTrans, _options);
        }

public int active()
{
    const int tabSource = 4;
    int ret;

    ret = super();

    if (tab.tab() == tabSource)
    {
        element.researchSourceTable();
    }

    return ret;
}

void executeQuery()
{
    switch (showOpen.value())
    {
        case 1 :
            criteriaOpen.value(strFmt("@GLS111260", dateNull()));
            break;
        case 0 :
            criteriaOpen.value("");
            break;
    }
    super();
}

void init()
{
    Query                query;
    QueryBuildDataSource qBd;

    super();

    if (element.args().parmObject())
    {
        query = new Query(element.args().parmObject());
        this.query(query);
    }

    qBd = this.query().dataSourceTable(tableNum(EmplTrans_RU));
    criteriaOpen = qBd.addRange(fieldNum(EmplTrans_RU, Closed));

    switch (element.args().dataset())
    {
        case tableNum(EmplSettlement_RU):
            qBd.clearDynalinks();
            if (element.args().parmEnum())
            {
                qBd.addDynalink(fieldNum(EmplTrans_RU, RecId), element.args().record(), fieldNum(EmplSettlement_RU, CreditRecId));
            }
            else
            {
                qBd.addDynalink(fieldNum(EmplTrans_RU, RecId), element.args().record(), fieldNum(EmplSettlement_RU, DebitRecId));
            }
            break;

        case tableNum(PurchBookVATProcessLogTrans_RU):
            qBd.clearDynalinks();
            qBd.addDynalink(fieldNum(EmplTrans_RU, EmplAccount), element.args().record(), fieldNum(PurchBookVATProcessLogTrans_RU, AccountNum));
            break;
    }
}

public void tabChanged(int _fromTab, int _toTab)
{
    element.researchSourceTable();
    super(_fromTab, _toTab);
}
