/// <summary>
/// Applies a filter based on the ItemId of a sub-component.
/// </summary>
public void applyBOMItemFilter()
{
    boolean itemFilterSet = (bomItemIdFilter.text() != '');

    if (itemFilterSet)
    {
        if (rangeBOMItemId == null)
        {
            qbdsBOMExists = engChgCaseBOMRouteTmp_ds.queryBuildDataSource().addDataSource(tableNum(BOM));
            qbdsBOMExists.joinMode(JoinMode::ExistsJoin);
            qbdsBOMExists.addLink(fieldNum(EngChgCaseBOMRouteTmp, EntityId), fieldNum(BOM, bomId), engChgCaseBOMRouteTmp_ds.name());
            rangeBOMItemId = qbdsBOMExists.addRange(fieldNum(BOM, ItemId));
        }

        qbdsBOMExists.enabled(true);
        rangeBOMItemId.value(queryValue(bomItemIdFilter.text()));
    }
    else
    {
        qbdsBOMExists.enabled(false);
    }

    boMsFormulasCheckBox.allowEdit(!itemFilterSet);
    routesCheckBox.allowEdit(!itemFilterSet);

    element.applyFilterBOMRoute();
}

/// <summary>
/// Applies a filter on the bom/route grid based on the selected filtering options.
/// </summary>
public void applyFilterBOMRoute()
{
    str rangeVal = SysQuery::valueEmptyString();

    if (bomItemIdFilter.text() != '')
    {
        // Only show BOMS as these are the only ones that can have BOM items
        rangeVal = queryValue(enum2int(CaseEntityType::BOM));
    }
    else
    {
        if (boMsFormulasCheckBox.value())
        {
            rangeVal = queryValue(enum2int(CaseEntityType::BOM));
        }
        if (routesCheckBox.value())
        {
            rangeVal = queryRangeConcat(rangeVal, queryValue(enum2int(CaseEntityType::Route)));
        }
    }

    rangeBOMRouteEntityType.value(rangeVal);
    caseAssociationBOMRoute_ds.executeQuery();
}

/// <summary>
/// Applies a filter on the version grid based on the selected filtering options.
/// </summary>
public void applyFilterVersion()
{
    str rangeActiveVal = SysQuery::valueUnlimited();
    str rangeApprovedVal = SysQuery::valueUnlimited();

    switch (versionsFilterRadioButton.selection())
    {
        case EngChgCaseBOMRouteVersionRadioButton::Active:
            rangeActiveVal = queryValue(enum2int(NoYes::Yes));
            break;
        case EngChgCaseBOMRouteVersionRadioButton::NotActive:
            rangeActiveVal = queryValue(enum2int(NoYes::No));
            break;
        case EngChgCaseBOMRouteVersionRadioButton::Approved:
            rangeApprovedVal = queryValue(enum2int(NoYes::Yes));
            break;
        case EngChgCaseBOMRouteVersionRadioButton::NotApproved:
            rangeApprovedVal = queryValue(enum2int(NoYes::No));
            break;
    }

    rangeVersionActive.value(rangeActiveVal);
    rangeVersionApproved.value(rangeApprovedVal);

    engChgCaseBOMRouteVersionApproval_ds.executeQuery();
}

/// <summary>
/// Binds the form data source to the temporary tables in the helper.
/// </summary>
public void bindToTmpTables()
{
    engChgCaseBOMRouteTmp.linkPhysicalTableInstance(approvalHelper.engChgCaseBOMRouteTmp());
    engChgCaseBOMRouteVersionTmp.linkPhysicalTableInstance(approvalHelper.engChgCaseBOMRouteVersionTmp());
}

