}

/// <summary>
/// Enable the navigation buttons if product change case:
/// - ValidateChange, ApplyAction and SuggestAction buttons: BOM, formula or route is associated
/// - ValidateChange button: One or more validation rules are enabled in the case category setup form
/// </summary>
void enableButtonControls()
{
    CaseAssociation     caseAssociation;
    boolean             validationRulesEnabled = EngChgCaseCategoryValidationRules::rulesEnabledForCaseCategory(CaseDetailBase::findRec(approvalHelper.parmCaseDetailBaseRecId()).caseCategory().RecId);

    select firstOnly EntityType, RecId from caseAssociation
        where caseAssociation.CaseRecId == approvalHelper.parmCaseDetailBaseRecId()
              && (caseAssociation.EntityType == CaseEntityType::BOM
              || caseAssociation.EntityType == CaseEntityType::Route);

    if (!caseAssociation)
    {
        ValidateChangeButton.enabled(validationRulesEnabled);
        SuggestActionsButton.enabled(false);
        StatusBarApplyActionsMenuItemButton.enabled(false);
    }
}

public void init()
{
    boolean versionVisible;

    if (element.args().dataset() != tableNum(CaseDetailBase))
    {
        throw error("@SYS104799");
    }

    approvalHelper = EngChgCaseBOMRouteApprovalHelper::newApprovalHelperAndLoadCaseAssociation(element.args().record().RecId);

    super();

    xSysLastValue::getLast(this);
    this.showFormulaVersionFields(showFormulaFields);
    this.enableButtonControls();

    versionVisible = new DictTable(tableNum(BOMVersion)).rights() >= AccessType::View
                        && isConfigurationkeyEnabled(configurationKeyNum(BOMVersion));

    element.updateDesign(InventDimFormDesignUpdate::Init);

    element.bindToTmpTables();
}

