/// <summary>
/// Sets an action on all selected approval records.
/// </summary>
/// <param name="_action">
/// The action to set on the selected records.
/// </param>
public void setBOMRouteAction(EngChgCaseBOMRouteApprovalAction _action)
{
    container                   selectedRecordIds;
    EngChgCaseBOMRouteApproval  approvalSelected;
    Array                       recordsMarked;

    // Place list of selected records in a list
    approvalSelected = engChgCaseBOMRouteApproval_ds.getFirst(true);
    if (!approvalSelected)
    {
        approvalSelected = engChgCaseBOMRouteApproval_ds.cursor();
    }

    while (approvalSelected)
    {
        selectedRecordIds += approvalSelected.RecId;
        approvalSelected = engChgCaseBOMRouteApproval_ds.getNext();
    }

    // Perform the update
    EngChgCaseBOMRouteApprovalHelper::setBOMRouteAction(selectedRecordIds, _action);

    // Refresh the view retaining the marked records
    recordsMarked = caseAssociationBOMRoute_ds.recordsMarked();
    caseAssociationBOMRoute_ds.research(true);
    caseAssociationBOMRoute_ds.markRecords(recordsMarked);
}

/// <summary>
/// Sets an action on all selected approval version records.
/// </summary>
/// <param name="_action">
/// The action to set on the selected records.
/// </param>
public void setVersionAction(EngChgCaseBOMRouteVersionApprovalAction _action)
{
    container                           selectedRecordIds;
    EngChgCaseBOMRouteVersionApproval   approvalSelected;
    Array                               recordsMarked;

    // Place list of selected records in a list
    approvalSelected = engChgCaseBOMRouteVersionApproval_ds.getFirst(true);
    if (!approvalSelected)
    {
        approvalSelected = engChgCaseBOMRouteVersionApproval_ds.cursor();
    }

    while (approvalSelected)
    {
        selectedRecordIds += approvalSelected.RecId;
        approvalSelected = engChgCaseBOMRouteVersionApproval_ds.getNext();
    }

    // Perform the update
    EngChgCaseBOMRouteApprovalHelper::setVersionAction(selectedRecordIds, _action);

    // Refresh the view retaining the marked records and cursor position
    recordsMarked = engChgCaseBOMRouteVersionApproval_ds.recordsMarked();
    engChgCaseBOMRouteVersionApproval_ds.research(true);
    engChgCaseBOMRouteVersionApproval_ds.markRecords(recordsMarked);
}

