public void suggestActions()
{
    Array   tableGroupGridMarkedRecords = caseAssociationBOMRoute_ds.recordsMarked();

    versionGroupGridMarkedRecords   = engChgCaseBOMRouteVersionApproval_ds.recordsMarked();

    approvalHelper.suggestActions();

    caseAssociationBOMRoute_ds.research(true);
    caseAssociationBOMRoute_ds.markRecords(tableGroupGridMarkedRecords);
}

public boolean unpack(container packedClass)
{
    int version     = RunBase::getVersion(packedClass);

    switch (version)
    {
        case #CurrentVersion:
            [version,#CurrentList] = packedClass;
            return true;
        default :
            return false;
    }
}

void updateDesign(InventDimFormDesignUpdate   _mode)
{
    InventDimParm   inventDimParmShow;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (! inventDimFormSetupBOMRouteVersion)
            {
                inventDimFormSetupBOMRouteVersion = InventDimCtrl::newFromForm(element);
            }

            inventDimParmShow.initDimBOMRouteVersion();
            inventDimFormSetupBOMRouteVersion.parmDimParmVisibleGrid(inventDimParmShow);
            inventDimFormSetupBOMRouteVersion.formSetControls(true);
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

edit EngChgCasePlannedEffectiveDate casePlannedEffectiveDateEdit(boolean _set, EngChgCasePlannedEffectiveDate _plannedEffectiveDate)
