}

public void init()
{
    super();

    rangeEntityCaseAssociation = engChgCaseBOMRouteVersionApproval_ds.queryBuildDataSource().addRange(fieldNum(EngChgCaseBOMRouteVersionApproval, EntityCaseAssociation));

    // Do not load anything to the datasource until the selection has been made in the upper grid
    rangeEntityCaseAssociation.value(queryValue(0));
}

public void selectionChanged()
{
    CaseAssociation  caseAssociationSelected;
    str              rangeVal;

    super();

    caseAssociationSelected = caseAssociationBOMRoute_ds.getFirst(true);
    if (!caseAssociationSelected)
    {
        caseAssociationSelected = caseAssociationBOMRoute_ds.cursor();
    }

    // Add all selected case associations from top grid as filter for what is shown in the lower grid
    while (caseAssociationSelected)
    {
        rangeVal = queryRangeConcat(rangeVal, queryValue(caseAssociationSelected.RecId));
        caseAssociationSelected = caseAssociationBOMRoute_ds.getNext();
    }

    if (!rangeVal)
    {
        // No records are present in the top grid so do not show anything in the lower grid
        rangeVal = queryValue(-1);
    }

    rangeEntityCaseAssociation.value(rangeVal);

    engChgCaseBOMRouteVersionApproval_ds.executeQuery();

    // if versions grid have any marked rows stored then we need to mark them again
    if (versionGroupGridMarkedRecords)
    {
        engChgCaseBOMRouteVersionApproval_ds.markRecords(versionGroupGridMarkedRecords);
        versionGroupGridMarkedRecords = null;
    }
}

