public void selectionChanged()
{
    BOMTable    bomTable;
    boolean     enableCoProducts = false;

    super();

    if (engChgCaseBOMRouteVersionTmp.EntityType == CaseEntityType::BOM)
    {
        bomTable = BOMTable::find(engChgCaseBOMRouteVersionTmp.EntityId);
        if (bomTable && bomTable.PmfBOMFormula == PmfBOMFormula::Formula)
        {
            enableCoProducts = true;
        }
    }

    versionsCoByProductsMenuItemButton.enabled(enableCoProducts);
}

