    {
        BOMVersion::jumpRefBOMVersionRecId(engChgCaseBOMRouteVersionApproval.EntityVersion, element);
    }
    else if (engChgCaseBOMRouteVersionApproval.EntityType == CaseEntityType::Route)
    {
        RouteVersion::jumpRefRouteVersionRecId(engChgCaseBOMRouteVersionApproval.EntityVersion, element);
    }
}

void clicked()
{
    Args                args = new Args();
    FormRun             formRun;
    BOMVersion          bomVersion = BOMVersion::findRecId(engChgCaseBOMRouteVersionTmp.EntityVersion);

    args.name(formStr(PmfFormulaCoBy));
    args.record(bomVersion);
    args.caller(this);

    formRun = classfactory.formRunClass(args);
    if (bomVersion && formHasMethod(formRun, identifierStr(addBOMVersionFilter)))
    {
        formRun.init();
        formRun.addBOMVersionFilter();
        formRun.run();
        if (!formRun.closed())
        {
            formRun.wait();
        }
    }
}

