[Form]
public class EximAuthorizationSchemesTable_IN extends FormRun
{
    NumberSeqFormHandler     numberSeqFormHandler;
    EximAuthorizationType_IN authorizationType;
    SysFormEnumComboBox      sysFormEnumComboBox;
    QueryBuildRange          authorizationTypeRange;
