}

    public void close()
{
    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodClose();
    }

    super();
}

    public void enableAttachButton()
{
    attach.enabled(eximAuthorizationSchemesTable.AuthorizationBasis != EximAuthorizationBasis_IN::Purchase
                   && (eximAuthorizationSchemesTable.Status == EximAuthorizationStatus_IN::Applied
                   || eximAuthorizationSchemesTable.Status  == EximAuthorizationStatus_IN::Created));
}

    public void enableAuthType(EximAuthorizationType_IN  _authorizationType)
{
    sysFormEnumComboBox.select(_authorizationType);
    authType.allowEdit(false);
}

    public void enableButtons()
{
    boolean isAuthorizationCreated;
    boolean isAuthorizationRejected;

    isAuthorizationCreated  = (eximAuthorizationSchemesTable.Status == EximAuthorizationStatus_IN::Created);
    isAuthorizationRejected = (eximAuthorizationSchemesTable.Status == EximAuthorizationStatus_IN::Rejected);

    functions.enabled(!isAuthorizationRejected);
    inquiry.enabled(!isAuthorizationRejected);
    summaryImpItem.enabled(!isAuthorizationCreated && !isAuthorizationRejected);
}

    public void enableControls()
{
    boolean isAuthorizationApproved;
    boolean isAuthorizationMerged;
    boolean isAuthorizationSplit;

    isAuthorizationApproved = (eximAuthorizationSchemesTable.Status == EximAuthorizationStatus_IN::Approved);
    isAuthorizationMerged   = (eximAuthorizationSchemesTable.Status == EximAuthorizationStatus_IN::Merged);
    isAuthorizationSplit    = eximAuthorizationSchemesTable.SplitApproved;

    extension.enabled(isAuthorizationApproved && !isAuthorizationMerged);
    taxinformation.enabled(isAuthorizationApproved && !isAuthorizationMerged && !isAuthorizationSplit);
    itemdetails.enabled(isAuthorizationApproved && !isAuthorizationMerged && !isAuthorizationSplit);

    if (!isAuthorizationSplit
        && EximAuthorizationSchemeReference_IN::existsReferenceType(eximAuthorizationSchemesTable.RecId,
                                                                    EximMergeSplit_IN::None))
    {
        extension.enabled(false);
        taxinformation.enabled(false);
        itemdetails.enabled(false);
    }

    eximAuthorizationSchemesTable_Closed.enabled(!isAuthorizationMerged);

    eximAuthorizationSchemesTable_ds.allowDelete(eximAuthorizationSchemesTable.Status == EximAuthorizationStatus_IN::Created);
    EximAuthorizationSchemeReference_ds.allowcreate(!isAuthorizationApproved && !isAuthorizationMerged);
    EximAuthorizationSchemeReference_ds.allowEdit(!isAuthorizationApproved && !isAuthorizationMerged);
    EximAuthorizationSchemeReference_ds.allowDelete(!isAuthorizationApproved && !isAuthorizationMerged);
}

    public void enableSalesPurchControls()
{
    EximParameters_IN eximParameters;
    boolean           isPurchaseAllowed;
    boolean           isSaleAllowed;

    eximParameters = EximParameters_IN::find();

    switch (authorizationType)
    {
        case EximAuthorizationType_IN::AA:
            isPurchaseAllowed = eximParameters.aaAllowPurchase;
            isSaleAllowed     = eximParameters.aaAllowSale;
            break;

        case EximAuthorizationType_IN::DFIA:
            isPurchaseAllowed = eximParameters.dfiaAllowPurchase;
            isSaleAllowed     = eximParameters.dfiaAllowSale;
            break;
    }

    eximAuthorizationSchemesLine_Purchvalue.visible(isPurchaseAllowed);
    eximAuthorizationSchemesLine_SaleValue.visible(isSaleAllowed);
    eximAuthorizationSchemesLine_Text.visible((isPurchaseAllowed || isSaleAllowed));
}

    public void init()
{
    Set         allowedValues = new Set(Types::Enum);
    SysDictEnum sysDictEnum   = new SysDictEnum(enumnum(EximAuthorizationType_IN));
    Integer     enumCount;
    EximAuthorizationListPageInteraction    eximAuthorizationListPageInteraction;
    EximAuthorizationSchemesTable_IN        eximAuthorizationSchemesTableLoc;
    EximAuthorizationType_IN                eximAuthorizationType;

    if (!EximParameters_IN::checkParameters())
    {
        throw error(strfmt("@GLS61668", "@GLS61572","@GLS61510", "@GLS61540" ));
    }

    if (element.args().callerFormControl().name() == formControlStr(EximAuthorizationSchemesTableListPage_IN, NewEximAuthorizationSchemes))
    {
        eximAuthorizationListPageInteraction = element.args().caller().PageInteraction();
        eximAuthorizationType = eximAuthorizationListPageInteraction.parmAuthType();
    }
    else
    {
        eximAuthorizationSchemesTableLoc = element.args().record();
        eximAuthorizationType = eximAuthorizationSchemesTableLoc.AuthorizationType;
    }

    for (enumCount = 0; enumCount < enumcnt(EximAuthorizationType_IN); enumCount++)
    {
        if (enum2str(EximAuthorizationType_IN::AA)      == sysDictEnum.index2Name(enumCount)
            || enum2str(EximAuthorizationType_IN::DFIA) == sysDictEnum.index2Name(enumCount))
        {
            allowedValues.add(enumCount);
        }
    }
    sysFormEnumComboBox = SysFormEnumComboBox::newParameters(element,
                                                             element.controlId(formControlStr(EximAuthorizationSchemesTable_IN, AuthType)),
                                                             enumnum(EximAuthorizationType_IN),
                                                             allowedValues);

    super();

    element.enableAuthType(eximAuthorizationType);
    authorizationType = authType.selection();
    annualConsumption.visible(authorizationType == EximAuthorizationType_IN::AA);
    element.enableButtons();
    element.enableAttachButton();
    element.enableControls();
    element.enableSalesPurchControls();
    authType.setFocus();
}

    public NumberSeqFormHandler numberSeqFormHandler()
{
    RefRecId numberSequenceCode;

    if (!numberSeqFormHandler)
    {
        numberSequenceCode = sysFormEnumComboBox.selection() == EximAuthorizationType_IN::AA ?
                             EximParameters_IN::numRefAAAuthorizationId().NumberSequenceId     :
                             EximParameters_IN::numRefDfiaAuthorizationId().NumberSequenceId;

        numberSeqFormHandler = NumberSeqFormHandler::newForm(numberSequenceCode,
                                                             element,
                                                             eximAuthorizationSchemesTable_ds,
                                                             fieldnum(EximAuthorizationSchemesTable_IN, AuthorizationId));
    }
    return numberSeqFormHandler;
}

    public FormDataSource parmAuthSchemeDataSource(FormDataSource _authSchemeDataSource = eximAuthorizationSchemesTable_ds)
{
    authSchemeDataSource = _authSchemeDataSource;
    return authSchemeDataSource;
}

