[Form]
public class FBBookingPeriodWizard_BR extends FormRun
{
    FBBookingPeriodWizard_BR    sysWizard;
    Map                         parentTreeItemMap;

    private const str Separator = '#';

}

/// <summary>
/// Adds the selected fiscal establishment avaliable to the list of the fiscal establishments selected
/// </summary>
public void add()
{
    FormTreeItem    treeItem;

    treeItem = Tree.getItem(Tree.getSelection());

    if (treeItem.children() > 0)
    {
        this.addTreeItemParentToListView(treeItem);
    }
    else
    {
        this.addTreeItemChildToListView(treeItem);
    }

    this.validateStep1();

    this.enableButtons();
}

/// <summary>
/// Adds all fiscal establishments avaliable to the list of the fiscal establishments selected
/// </summary>
public void addAll()
{
    TreeItemIdx     parentIdx;
    FormTreeItem    treeItem;

    parentIdx = Tree.getRoot();

    while (parentIdx)
    {
        treeItem = Tree.getItem(parentIdx);

        this.addTreeItemParentToListView(treeItem);

        parentIdx = Tree.getRoot();
    }

    this.validateStep1();

    this.enableButtons();
}

/// <summary>
/// Adds a fiscal establishment from the selected list to the available list
/// </summary>
/// <param name="_listItem">
/// A <c>FormListItem</c> object
/// </param>
private void addListItemToTree(FormListItem _listItem)
{
    TreeItemIdx     parentIdx;
    str             dataStr;
    str             parentText;
    container       dataCon;

    dataStr = _listItem.data();

    dataCon = str2con(dataStr, Separator);

    parentText = conPeek(dataCon, 1);

    if (parentTreeItemMap.exists(parentText))
    {
        parentIdx = parentTreeItemMap.lookup(parentText);
    }
    else // Add a parent tree item
    {
        parentIdx = this.addTreeItemParent(parentText);
    }

    this.addTreeItemChild(parentIdx, _listItem.text(), dataStr);

    ListView.delete(_listItem.idx());
}

/// <summary>
/// Adds a fiscal establishment to the available list
/// </summary>
/// <param name="_parentIdx">
/// The parent index
/// </param>
/// <param name="_text">
/// The child text
/// </param>
/// <param name="_dataStr">
/// The child data
/// </param>
/// <returns>
/// The added <c>FormTreeItem</c> object
/// </returns>
private FormTreeItem addTreeItemChild(TreeItemIdx _parentIdx, str _text, str _dataStr)
{
    FormTreeItem childTreeItem;

    childTreeItem = new FormTreeItem(_text, 0, 0, _dataStr);

    Tree.addItem(_parentIdx, FormTreeAdd::Sort, childTreeItem);

    return childTreeItem;
}

/// <summary>
/// Adds a fiscal establishment from the available list to the selected list
/// </summary>
/// <param name="_childTreeItem">
/// A <c>FormTreeItem</c> object
/// </param>
private void addTreeItemChildToListView(FormTreeItem _childTreeItem)
{
    FormListItem    listItem;
    FormTreeItem    parentTreeItem;

    parentTreeItem = Tree.getItem(Tree.getParent(_childTreeItem.idx()));

    listItem = new FormListItem(_childTreeItem.text(), _childTreeItem.image(), _childTreeItem.data());

    ListView.addItem(listItem);

    Tree.delete(_childTreeItem.idx());

    if (Tree.getChild(parentTreeItem.idx()) == 0)
    {
        Tree.delete(parentTreeItem.idx());

        parentTreeItemMap.remove(parentTreeItem.text());
    }
}

/// <summary>
/// Adds a fiscal organization to the available list like a parent
/// </summary>
/// <param name="_text">
/// The parent text
/// </param>
/// <returns>
/// The parent index
/// </returns>
private TreeItemIdx addTreeItemParent(str _text)
{
    TreeItemIdx     parentIdx;
    FormTreeItem    parentTreeItem;

    parentTreeItem = new FormTreeItem(_text, 0, 1);
    parentTreeItem.stateBold(true);

    parentIdx = Tree.addItem(0, FormTreeAdd::Sort, parentTreeItem);

    parentTreeItemMap.insert(_text, parentIdx);

    return parentIdx;
}

/// <summary>
/// Adds all fiscal establishments of the fiscal organization
/// </summary>
/// <param name="_parentTreeItem">
/// A <c>FormTreeItem</c> object parent
/// </param>
private void addTreeItemParentToListView(FormTreeItem _parentTreeItem)
{
    FormTreeItem    childTreeItem;

    TreeItemIdx     parentIdx   = _parentTreeItem.idx();
    TreeItemIdx     childIdx    = Tree.getChild(parentIdx);

    while (Tree.getParent(childIdx) == parentIdx)
    {
        childTreeItem = Tree.getItem(childIdx);

        childIdx = Tree.getNextSibling(childIdx);

        this.addTreeItemChildToListView(childTreeItem);
    }
}

/// <summary>
/// Enables or disables the buttons of the first step tab
/// </summary>
private void enableButtons()
{
    boolean enableAddButtons;
    boolean enableRemoveButtons;

    boolean existsTreeChild()
    {
        TreeItemIdx     parentIdx;

        parentIdx = Tree.getRoot();

        while (parentIdx != 0)
        {
            if (Tree.getChild(parentIdx) != 0)
            {
                return true;
            }

            parentIdx = Tree.getNextSibling(parentIdx);
        }

        return false;
    }

    enableAddButtons = existsTreeChild();

    AddButton.enabled(enableAddButtons);
    AddAllButton.enabled(enableAddButtons);

    enableRemoveButtons = ListView.getCount();

    RemoveButton.enabled(enableRemoveButtons);
    RemoveAllButton.enabled(enableRemoveButtons);
}

/// <summary>
/// Enables or disables the IPIAssessmentPeriodicity control
/// </summary>
public void enableIIPITaxAssessmentPeriodicity()
{
    IPIAssessmentPeriodicity.enabled(TaxAssessmentIPI.checked());
}

/// <summary>
/// Fills the <c>FormGridControl</c> with the booking period that will be generated
/// </summary>
private void fillGridView()
{
    FormListItem    listItem;
    ListItemIdx     listItemIdx;

    str             dataStr;
    container       dataCon;
    PeriodDate      periodDate;
    PeriodDate      periodFrom = mkDate(1, FromMonth.selection(), FromYear.value());
    periodDate      periodTo = mkDate(1, ToMonth.selection(), ToYear.value());

    delete_from FBBookingPeriodWizardTmp_BR;

    ttsBegin;

    for (listItemIdx = 0; listItemIdx < ListView.getCount(); listItemIdx++)
    {
        listItem = ListView.getItem(listItemIdx);

        dataStr = listItem.data();

        dataCon = str2con(dataStr, Separator, false);

        for (periodDate = periodFrom; periodDate <= periodTo; periodDate = dateMthFwd(periodDate, 1))
        {
            FBBookingPeriodWizardTmp_BR.FiscalEstablishmentId = conPeek(dataCon, 2);
            FBBookingPeriodWizardTmp_BR.FiscalEstablishmentDataArea = conPeek(dataCon, 3);
            FBBookingPeriodWizardTmp_BR.PeriodDate = periodDate;
            FBBookingPeriodWizardTmp_BR.insert();
        }
    }

    FBBookingPeriodWizardTmp_BR_ds.executeQuery();

    ttsCommit;
}

public void init()
{
    super();

    if (element.args().caller())
    {
        sysWizard = element.args().caller();
    }
    else
    {
        throw error("@SYS22996");
    }

    this.initTree();
    this.initControl();
    this.enableButtons();
}

/// <summary>
/// Initializes the controls
/// </summary>
private void initControl()
{
    FBBookingPeriodMonth_BR currentMonth = mthOfYr(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()));
    FBBookingPeriodYear_BR currentYear = year(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()));

    FromMonth.selection(currentMonth);
    FromYear.value(currentYear);

    ToMonth.selection(currentMonth);
    ToYear.value(currentYear);

    element.setPeriodFrom();
    element.setPeriodTo();

    IPIAssessmentPeriodicity.enabled(false);
}

/// <summary>
/// Initializes the fiscal establishments to the available list of the first step tab
/// </summary>
private void initTree()
{
    const str NameSeparator = '-';

    FiscalEstablishment_BR                  fiscalEstablishmentRoot;
    FiscalEstablishment_BR                  fiscalEstablishment;
    FiscalOrganization_BR                   fiscalOrganization;

    TreeItemIdx                             parentIdx;
    str                                     dataStr;
    str                                     parentText;
    str                                     childText;

    parentTreeItemMap = new Map(Types::String, Types::Integer);

    while select crossCompany dataAreaId, FiscalEstablishmentId, Name
        from fiscalEstablishmentRoot order by dataAreaId, Name
        join RecId from fiscalOrganization
            where fiscalOrganization.FiscalEstablishmentId == fiscalEstablishmentRoot.FiscalEstablishmentId
                && fiscalOrganization.FiscalEstablishmentDataArea == fiscalEstablishmentRoot.dataAreaId
    {
        // Add a parent tree item

        parentText = fiscalEstablishmentRoot.dataAreaId + NameSeparator + fiscalEstablishmentRoot.Name;

        parentIdx = this.addTreeItemParent(parentText);

        while select crossCompany dataAreaId, FiscalEstablishmentId
            from fiscalEstablishment order by dataAreaId, FiscalEstablishmentId
            where fiscalEstablishment.FiscalOrganization_BR == fiscalOrganization.RecId
        {
            // Add a child tree item

            childText = fiscalEstablishment.dataAreaId + NameSeparator + fiscalEstablishment.FiscalEstablishmentId;

            dataStr = parentText + Separator + fiscalEstablishment.FiscalEstablishmentId + Separator + fiscalEstablishment.dataAreaId;

            this.addTreeItemChild(parentIdx, childText, dataStr);
        }
    }
}

/// <summary>
/// Removes the selected fiscal establishment from the fiscal establishments selected list to the list of the fiscal establishments available
/// </summary>
public void remove()
{
    FormListItem    listItem;
    ListItemIdx     listItemIdx;

    for (listItemIdx = 0; listItemIdx < ListView.getCount(); listItemIdx++)
    {
        listItem = ListView.getItem(listItemIdx);

        if (listItem.stateSelected())
        {
            this.addListItemToTree(listItem);

            break;
        }
    }

    this.validateStep1();

    this.enableButtons();
}

/// <summary>
/// Removes all fiscal establishments from the fiscal establishments selected list to the list of the fiscal establishments available
/// </summary>
public void removeAll()
{
    FormListItem    listItem;
    ListItemIdx     listItemIdx;

    while (ListView.getCount() > 0)
    {
        listItemIdx = ListView.getTopIndex();

        listItem = ListView.getItem(listItemIdx);

        this.addListItemToTree(listItem);
    }

    this.validateStep1();

    this.enableButtons();
}

/// <summary>
/// Sets the period from date to be validated in the <c>FBBookingPeriodWizard_BR</c> class
/// </summary>
public void setPeriodFrom()
{
    sysWizard.parmPeriodFrom(mkDate(1, FromMonth.selection(), FromYear.value()));
}

/// <summary>
/// Sets the period to date to be validated in the <c>FBBookingPeriodWizard_BR</c> class
/// </summary>
public void setPeriodTo()
{
    sysWizard.parmPeriodTo(mkDate(1, ToMonth.selection(), ToYear.value()));
}

/// <summary>
/// Sets the selected tax assessments in the finish tab
/// </summary>
private void setTaxAssessmentSelection()
{
    ICMS.checked(TaxAssessmentICMS.checked());
    ICMSST.checked(TaxAssessmentICMSST.checked());
    IPI.checked(TaxAssessmentIPI.checked());
    ISS.checked(TaxAssessmentISS.checked());
}

/// <summary>
/// Sets the parameters necessary to run the booking period generate process
/// </summary>
private void setWizardParms()
{
    sysWizard.parmFBBookingPeriodWizardTmp_BR(FBBookingPeriodWizardTmp_BR);

    sysWizard.parmCreateTaxAssessmentICMS(TaxAssessmentICMS.checked());
    sysWizard.parmCreateTaxAssessmentICMSST(TaxAssessmentICMSST.checked());
    sysWizard.parmCreateTaxAssessmentIPI(TaxAssessmentIPI.checked());
    sysWizard.parmIPIAssessmentPeriodicity(IPIAssessmentPeriodicity.selection());
    sysWizard.parmCreateTaxAssessmentISS(TaxAssessmentISS.checked());
}

/// <summary>
/// Updates the finish step tab controls
/// </summary>
public void updateFinishStep()
{
    element.fillGridView();

    element.setWizardParms();

    element.setTaxAssessmentSelection();
}

/// <summary>
/// Validates the step 1
/// </summary>
public void validateStep1()
{
    sysWizard.nextEnabled(ListView.getCount());
}

