Method.Source:
FiscalEstablishment_BR getFiscalEstablishment()
{
    FBTaxAssessment_BR taxAssessment;

    select taxAssessment
        where taxAssessment.RecId == FBCIAPAssessment_BR.FBTaxAssessment_BR;

    return taxAssessment.bookingPeriod().fiscalEstablishment();
}

public void refreshTmpFBCIAPAssetTrans_FiscalDoc()
{
    FBCIAPAssetTrans_FiscalDoc_BR   ciapAssetTrans_FiscalDoc_BR;
    Fiscaldocument_BR               fiscalDocument;
    FiscalDocumentLine_BR           fiscaldocumentLine;
    ExternalFiscalDocument_BR       externalFiscalDocument;
    ExternalFiscalDocumentLine_BR   externalFiscalDocumentLine;

    if (LineFiscalDocuments.isActivePage() || LineFiscalDocuments.isExpanded())
    {
        delete_from TmpFBCIAPAssetTrans_FiscalDoc_BR;

        while select * from ciapAssetTrans_FiscalDoc_BR
            where ciapAssetTrans_FiscalDoc_BR.FBCIAPAssetTrans_BR == FBCIAPAssetTrans_BR.RecId
        {
            if (ciapAssetTrans_FiscalDoc_BR.FiscalDocument_BR)
            {
                changeCompany (FBTaxAssessment_BR.bookingPeriod().FiscalEstablishmentDataArea)
                {
                    select fiscalDocument
                        where fiscalDocument.RecId == ciapAssetTrans_FiscalDoc_BR.FiscalDocument_BR
                        outer join LineNum, ItemId from fiscaldocumentLine
                            where fiscaldocumentLine.FiscalDocument == fiscalDocument.RecId
                                && fiscaldocumentLine.RecId == ciapAssetTrans_FiscalDoc_BR.FiscalDocumentLine_BR;
                }

                TmpFBCIAPAssetTrans_FiscalDoc_BR.initFromReferenceMap(fiscalDocument);

                TmpFBCIAPAssetTrans_FiscalDoc_BR.RefRecId   = ciapAssetTrans_FiscalDoc_BR.RecId;
                TmpFBCIAPAssetTrans_FiscalDoc_BR.RefTableId = ciapAssetTrans_FiscalDoc_BR.TableId;

                TmpFBCIAPAssetTrans_FiscalDoc_BR.LineNum = fiscaldocumentLine.LineNum;
                TmpFBCIAPAssetTrans_FiscalDoc_BR.ItemId = fiscaldocumentLine.ItemId;

                TmpFBCIAPAssetTrans_FiscalDoc_BR.CIAPAssetTransFiscalDoc = ciapAssetTrans_FiscalDoc_BR.RecId;

                TmpFBCIAPAssetTrans_FiscalDoc_BR.insert();
            }
            else if (ciapAssetTrans_FiscalDoc_BR.ExternalFiscalDocument_BR)
            {
                changeCompany (FBTaxAssessment_BR.bookingPeriod().FiscalEstablishmentDataArea)
                {
                    select externalFiscalDocument
                        where externalFiscalDocument.RecId == ciapAssetTrans_FiscalDoc_BR.ExternalFiscalDocument_BR
                            outer join externalFiscalDocumentLine
                                where externalFiscalDocumentLine.ExternalFiscalDocument_BR == externalFiscalDocument.RecId;
                }

                TmpFBCIAPAssetTrans_FiscalDoc_BR.initFromReferenceMap(externalFiscalDocument);
                TmpFBCIAPAssetTrans_FiscalDoc_BR.ItemId = externalFiscalDocumentLine.ItemId;
                TmpFBCIAPAssetTrans_FiscalDoc_BR.LineNum = externalFiscalDocumentLine.LineNum;
                TmpFBCIAPAssetTrans_FiscalDoc_BR.RefRecId   = externalFiscalDocument.RecId;
                TmpFBCIAPAssetTrans_FiscalDoc_BR.RefTableId = externalFiscalDocument.TableId;

                TmpFBCIAPAssetTrans_FiscalDoc_BR.CIAPAssetTransFiscalDoc = ciapAssetTrans_FiscalDoc_BR.RecId;

                TmpFBCIAPAssetTrans_FiscalDoc_BR.insert();
            }
        }
    }
}

public int active()
{
    int ret;

    void enableDataSource(FormDataSource _datasource, boolean _edit)
    {
        _datasource.allowCreate(_datasource.allowCreate() && _edit);
        _datasource.allowDelete(_datasource.allowCreate() && _edit);
        _datasource.allowEdit  (_datasource.allowCreate() && _edit);
    }

    ret = super();

    FBCIAPAssetTrans_BR_ds.allowEditChildDataSources();

    enableDataSource(FBCIAPAssessment_BR_ds             , FBTaxAssessment_BR.Status != FBTaxAssessmentStatus_BR::Closed);
    enableDataSource(FBCIAPAssetTrans_BR_ds             , FBTaxAssessment_BR.Status != FBTaxAssessmentStatus_BR::Closed);
    enableDataSource(TmpFBCIAPAssetTrans_FiscalDoc_BR_ds, FBTaxAssessment_BR.Status != FBTaxAssessmentStatus_BR::Closed);
    enableDataSource(FBCIAPAssetTrans_OtherCredits_BR_ds, FBTaxAssessment_BR.Status != FBTaxAssessmentStatus_BR::Closed);

    //EditRecordButton.enabled    (FBTaxAssessment_BR.Status != FBTaxAssessmentStatus_BR::Closed);

    return ret;
}

public void updateAmounts()
{
    FBCIAPAssessment_BR.updateBalances();
    FBCIAPAssessment_BR.update();

    if (LineViewHeader.isActivePage() || LineViewHeader.isExpanded())
    {
        FBCIAPAssessment_BR_ds.reread();
        FBCIAPAssessment_BR_ds.refresh();
    }
}

public void updateDesign()
{
    boolean allowEdit;

    allowEdit = FBCIAPAssetTrans_BR.TransType == FBCIAPAssetTransType_BR::IM
        || FBCIAPAssetTrans_BR.TransType == FBCIAPAssetTransType_BR::ATSale
        || FBCIAPAssetTrans_BR.TransType == FBCIAPAssetTransType_BR::ATTransfer
        || FBCIAPAssetTrans_BR.TransType == FBCIAPAssetTransType_BR::OT
        || FBCIAPAssetTrans_BR.TransType == FBCIAPAssetTransType_BR::SI
        || FBCIAPAssetTrans_BR.TransType == FBCIAPAssetTransType_BR::PE;

    FBCIAPAssetTrans_BR_ds.object(fieldnum(FBCIAPAssetTrans_BR, ICMSAmount))        .allowEdit(allowEdit);
    FBCIAPAssetTrans_BR_ds.object(fieldnum(FBCIAPAssetTrans_BR, ICMSDiffAmount))    .allowEdit(allowEdit);
    FBCIAPAssetTrans_BR_ds.object(fieldnum(FBCIAPAssetTrans_BR, ICMSFreightAmount)) .allowEdit(allowEdit);
    FBCIAPAssetTrans_BR_ds.object(fieldnum(FBCIAPAssetTrans_BR, ICMSSTAmount))      .allowEdit(allowEdit);
}

public void write()
{
    super();

    FBCIAPAssessment_BR_ds.updateAmounts();

    this.allowEditChildDataSources();
}

public int active()
{
    int ret;

    ret = super();

    if (FBCIAPAssetTrans_BR.RecId)
    {
        FBCIAPAssetTrans_BR_ds.updateDesign();

        element.refreshTmpFBCIAPAssetTrans_FiscalDoc();
    }

    this.allowEditChildDataSources();

    return ret;
}

public void initValue()
{
    super();

    TmpFBCIAPAssetTrans_FiscalDoc_BR.FiscalDocumentDataArea = FBTaxAssessment_BR.bookingPeriod().FiscalEstablishmentDataArea;

    if (FBCIAPAssetTrans_BR.TransType == FBCIAPAssetTransType_BR::IM)
    {
        TmpFBCIAPAssetTrans_FiscalDoc_BR.FiscalDocumentAccountTypeRef   = CustVendType_BR::Vendor;
        TmpFBCIAPAssetTrans_FiscalDoc_BR.FiscalDocumentDirectionRef     = FiscalDocDirection_BR::Incoming;
        TmpFBCIAPAssetTrans_FiscalDoc_BR.FiscalDocumentIssuerRef        = FiscalDocumentIssuer_BR::ThirdParty;
    }
}

public void write()
{
    FBCIAPAssetTrans_FiscalDoc_BR ciapAssetTrans_FiscalDoc;

    ttsBegin;

    if (!TmpFBCIAPAssetTrans_FiscalDoc_BR.RecId)
    {
        TmpFBCIAPAssetTrans_FiscalDoc_BR.insert();

        ciapAssetTrans_FiscalDoc.FBCIAPAssetTrans_BR = FBCIAPAssetTrans_BR.RecId;
        ciapAssetTrans_FiscalDoc.FBCIAPAssetTable_BR = FBCIAPAssetTrans_BR.FBCIAPAssetTable_BR;
        ciapAssetTrans_FiscalDoc.ExternalFiscalDocument_BR = TmpFBCIAPAssetTrans_FiscalDoc_BR.RefRecId;
        ciapAssetTrans_FiscalDoc.insert();
    }
    else
    {
        TmpFBCIAPAssetTrans_FiscalDoc_BR.update();
    }

    TmpFBCIAPAssetTrans_FiscalDoc_BR_ds.reread();
    TmpFBCIAPAssetTrans_FiscalDoc_BR_ds.refresh();

    ttsCommit;
}

public void delete()
{
    ExternalFiscalDocument_BR       externalFiscalDocument;
    ExternalFiscalDocumentLine_BR   externalFiscalDocumentLine;
    FBCIAPAssetTrans_FiscalDoc_BR   ciapAssetTrans_FiscalDoc;

    ttsBegin;

    if (TmpFBCIAPAssetTrans_FiscalDoc_BR.RefTableId == tableNum(ExternalFiscalDocument_BR))
    {
        delete_from externalFiscalDocumentLine
            where externalFiscalDocumentLine.ExternalFiscalDocument_BR == TmpFBCIAPAssetTrans_FiscalDoc_BR.RefRecId;

        delete_from externalFiscalDocument
            where externalFiscalDocument.RecId == TmpFBCIAPAssetTrans_FiscalDoc_BR.RefRecId;
    }

    delete_from ciapAssetTrans_FiscalDoc
        where ciapAssetTrans_FiscalDoc.RecId == TmpFBCIAPAssetTrans_FiscalDoc_BR.CIAPAssetTransFiscalDoc;

    super();

    ttsCommit;
}
