public void refreshTmpFBCIAPAssetTrans_FiscalDoc()
{
    FBCIAPAssetTrans_FiscalDoc_BR   ciapAssetTrans_FiscalDoc_BR;
    Fiscaldocument_BR               fiscalDocument;
    ExternalFiscalDocument_BR       externalFiscalDocument;
    FBCIAPAssessment_BR             ciapAssessment;
    DataAreaId                      dataAreaId;

    if (LineFiscalDocuments.isActivePage() || LineFiscalDocuments.isExpanded())
    {
        select FBTaxAssessment_BR from ciapAssessment
            where ciapAssessment.RecId == FBCIAPAssetTrans_BR.FBCIAPAssessment_BR;

        dataAreaId = FBTaxAssessment_BR::find(ciapAssessment.FBTaxAssessment_BR).bookingPeriod().FiscalEstablishmentDataArea;

        delete_from TmpFBCIAPAssetTrans_FiscalDoc_BR;

        while select * from ciapAssetTrans_FiscalDoc_BR
            where ciapAssetTrans_FiscalDoc_BR.FBCIAPAssetTable_BR == FBCIAPAssetTrans_BR.FBCIAPAssetTable_BR
        {
            changeCompany(dataAreaId)
            {
                if (ciapAssetTrans_FiscalDoc_BR.FiscalDocument_BR)
                {
                    select fiscalDocument
                        where fiscalDocument.RecId == ciapAssetTrans_FiscalDoc_BR.FiscalDocument_BR;

                    TmpFBCIAPAssetTrans_FiscalDoc_BR.initFromReferenceMap(fiscalDocument);
                    TmpFBCIAPAssetTrans_FiscalDoc_BR.insert();
                }
                else if (ciapAssetTrans_FiscalDoc_BR.ExternalFiscalDocument_BR)
                {
                    select externalFiscalDocument
                        where externalFiscalDocument.RecId == ciapAssetTrans_FiscalDoc_BR.ExternalFiscalDocument_BR;

                    TmpFBCIAPAssetTrans_FiscalDoc_BR.initFromReferenceMap(externalFiscalDocument);
                    TmpFBCIAPAssetTrans_FiscalDoc_BR.insert();
                }
            }
        }

        TmpFBCIAPAssetTrans_FiscalDoc_BR_ds.executeQuery();
    }
}

public int active()
{
    int ret;

    ret = super();

    FBCIAPAssetTable_BR_ds.allowEdit(FBCIAPAssetTable_BR.Status != FBCIAPAssetStatus_BR::Closed);

    FBCIAPAssetTable_BR_ds.object(fieldNum(FBCIAPAssetTable_BR, Installments)).allowEdit(FBCIAPAssetTable_BR.recoveredInstallments() == 0);

    FBCIAPAssetTable_BR_ds.object(fieldNum(FBCIAPAssetTable_BR, ICMSAmount))        .allowEdit(FBCIAPAssetTable_BR.Status == FBCIAPAssetStatus_BR::NoAcquisition);
    FBCIAPAssetTable_BR_ds.object(fieldNum(FBCIAPAssetTable_BR, ICMSDiffAmount))    .allowEdit(FBCIAPAssetTable_BR.Status == FBCIAPAssetStatus_BR::NoAcquisition);
    FBCIAPAssetTable_BR_ds.object(fieldNum(FBCIAPAssetTable_BR, ICMSFreightAmount)) .allowEdit(FBCIAPAssetTable_BR.Status == FBCIAPAssetStatus_BR::NoAcquisition);
    FBCIAPAssetTable_BR_ds.object(fieldNum(FBCIAPAssetTable_BR, ICMSSTAmount))      .allowEdit(FBCIAPAssetTable_BR.Status == FBCIAPAssetStatus_BR::NoAcquisition);

    return ret;
}

public int active()
{
    int ret;

    ret = super();

    element.refreshTmpFBCIAPAssetTrans_FiscalDoc();

    return ret;
}
