[Form]
public class FBFiscalDocumentAdjustmentCodeICMS_BR extends FormRun implements OfficeIMenuCustomizer
{
    container   spedAdjustmentCode;
    container   spedAdjustmentCodeICMSDif;
    private boolean techNotes2019001110Enabled;
    private boolean benefitCodesValidationEnabled;
    private boolean isLiteralCode;

}

/// <summary>
/// Changes the tax type and generates the adjustment code updated
/// </summary>
public void changeTaxType()
{
    fbFiscalDocumentAdjustmentCodeICMS_BR.updateTaxType(ICMSType.selection());

    spedAdjustmentCodeICMSDif = conPoke(spedAdjustmentCodeICMSDif, 2, enum2int(fbFiscalDocumentAdjustmentCodeICMS_BR.getICMSType()));

    FBFiscalDocumentAdjustmentCodeICMS_BR.OccurrenceCode = "";

    FBFiscalDocumentAdjustmentCodeICMS_BR_DS.generateSpedAdjustmentCode();

    this.updateAdjustmentControls();
}

private void checkTechNotes2019001110Enabled()
{
    techNotes2019001110Enabled = FiscalEstablishment_BR::isTechnicalNoteEnabledForState(EFDocConstants_BR::NFeNT2019001v110, FBFiscalDocumentAdjustmentCodeICMS_BR.State);
}

private void checkBenefitCodesValidationEnabled()
{
    benefitCodesValidationEnabled = EFDocAuthorityState_BR::checkEnabledValidationBenefitCodes(FBFiscalDocumentAdjustmentCodeICMS_BR.State);
}

public void init()
{
    super();

    GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceBR10007, funcName());

    FBGeneralAdjustmentCodeICMSLedger_BR_LedgerDimension.parmPostingType(LedgerPostingType::Tax);
}

private void updateAdjustmentControls()
{
    if ((fbFiscalDocumentAdjustmentCodeICMS_BR.getICMSType() == FBICMSType_BR::ICMSDIF ||
        fbFiscalDocumentAdjustmentCodeICMS_BR.getICMSType() == FBICMSType_BR::ICMSDIFPovertyFund)
        && !isLiteralCode)
    {
        AdjustmentType.visible(false);
        AdjustmentTypeICMSDif.visible(true);
    }
    else
    {
        AdjustmentType.visible(true);
        AdjustmentType_AssessmentType.visible(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
        AdjustmentType_AssessmentType.enabled(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
        AdjustmentType_ResponsibilityType.visible(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
        AdjustmentType_ResponsibilityType.enabled(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
        AdjustmentType_CollectionType.visible(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
        AdjustmentType_CollectionType.enabled(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
        AdjustmentType_SourceTaxationType.visible(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
        AdjustmentType_SourceTaxationType.enabled(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
        GIAAdjustmentCode.visible(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
        GIAAdjustmentCode.enabled(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
        AdjustmentTypeICMSDif.visible(false);
        AdjustmentType_Classification.visible(!isLiteralCode);
        AdjustmentType_Classification.enabled(!isLiteralCode);
        AdjustmentType_OccurrenceCode.visible(!isLiteralCode);
        AdjustmentType_OccurrenceCode.enabled(!isLiteralCode);
        Payment.visible(!isLiteralCode);
        TabPagePosting.visible(!isLiteralCode);
    }

    FBFiscalDocAdjustmentCodeICMSLedger_BR_ds.allowCreate(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
    FBFiscalDocAdjustmentCodeICMSLedger_BR_ds.allowEdit(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
    FBFiscalDocAdjustmentCodeICMSLedger_BR_ds.allowDelete(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
}

/// <summary>
/// Updates the value in the ICMSType control
/// </summary>
public void updateICMSType()
{
    boolean recordExists = FBFiscalDocumentAdjustmentCodeICMS_BR.RecId;
    ICMSType.selection(!recordExists && isLiteralCode ? FBICMSType_BR::ICMS : FBFiscalDocumentAdjustmentCodeICMS_BR.getICMSType());
    ICMSType.allowEdit(!recordExists && !isLiteralCode);

    this.updateAdjustmentControls();
}

/// <summary>
/// Sets visible and editable for fields which are needed for literal adjustmnent code.
/// </summary>
public void updateLiteralCode()
{
    boolean recordExists = FBFiscalDocumentAdjustmentCodeICMS_BR.RecId;;
    boolean enableLiteralCode = techNotes2019001110Enabled && fbFiscalDocumentAdjustmentCodeICMS_BR.Table52 == NoYes::Yes;
    FBFiscalDocumentAdjustmentCodeICMS_BR_ds.object(fieldNum(FBFiscalDocumentAdjustmentCodeICMS_BR, LiteralCode)).enabled(enableLiteralCode);
    FBFiscalDocumentAdjustmentCodeICMS_BR_ds.object(fieldNum(FBFiscalDocumentAdjustmentCodeICMS_BR, LiteralCode)).visible(enableLiteralCode);

    if (fbFiscalDocumentAdjustmentCodeICMS_BR.LiteralCode == NoYes::Yes && !enableLiteralCode)
    {
        fbFiscalDocumentAdjustmentCodeICMS_BR.LiteralCode = NoYes::No;
    }

    isLiteralCode = techNotes2019001110Enabled && fbFiscalDocumentAdjustmentCodeICMS_BR.LiteralCode == NoYes::Yes;

    if (isLiteralCode)
    {
        fbFiscalDocumentAdjustmentCodeICMS_BR.OccurrenceCode = '';
    }
    else
    {
        FBFiscalDocumentAdjustmentCodeICMS_BR.SuppressAdjustmentCode = NoYes::No;
    }

    FBFiscalDocumentAdjustmentCodeICMS_BR_ds
        .object(fieldNum(FBFiscalDocumentAdjustmentCodeICMS_BR, SpedAdjustmentCode))
        .allowEdit(!recordExists && isLiteralCode && FBFiscalDocumentAdjustmentCodeICMS_BR.SuppressAdjustmentCode == NoYes::No);
    FBFiscalDocumentAdjustmentCodeICMS_BR_ds.object(fieldNum(FBFiscalDocumentAdjustmentCodeICMS_BR, LiteralCode)).visible(enableLiteralCode);
    FBFiscalDocumentAdjustmentCodeICMS_BR_ds.object(fieldNum(FBFiscalDocumentAdjustmentCodeICMS_BR, LiteralCode)).enabled(enableLiteralCode && !recordExists);
    FBFiscalDocumentAdjustmentCodeICMS_BR_ds.object(fieldNum(FBFiscalDocumentAdjustmentCodeICMS_BR, SuppressAdjustmentCode)).visible(isLiteralCode);
    FBFiscalDocumentAdjustmentCodeICMS_BR_ds.object(fieldNum(FBFiscalDocumentAdjustmentCodeICMS_BR, SuppressAdjustmentCode)).enabled(isLiteralCode && !recordExists);
}

