public void setDataAreaId()
{
    FBGeneralAdjustmentCodeICMSLedger_BR_LedgerDimension.setDataAreaId(FBFiscalDocAdjustmentCodeICMSLedger_BR.Company);
    FBGeneralAdjustmentCodeICMSLedger_BR_LedgerDimension.parmCurrentLedgerCOA(Ledger::chartOfAccounts(CompanyInfo::findDataArea(FBFiscalDocAdjustmentCodeICMSLedger_BR.Company).RecId));
}

public void updatePaymentControls()
{
    switch (FBFiscalDocumentAdjustmentCodeICMS_BR.Classification)
    {
        case FBFiscalDocumentClassificationICMS_BR::Debit:
        case FBFiscalDocumentClassificationICMS_BR::OtherDebit:
        case FBFiscalDocumentClassificationICMS_BR::SpecialPurposeDebit:
            CreatePaymentCheckbox.enabled(!FBFiscalDocumentAdjustmentCodeICMS_BR.Table52);
            break;

        default:
            CreatePaymentCheckbox.enabled(false);
            FBFiscalDocumentAdjustmentCodeICMS_BR.CreatePayment = NoYes::No;
            break;
    }

    RevenueCode.enabled(CreatePaymentCheckbox.checked());
}

public int active()
{
    int ret;

    ret = super();

    element.checkTechNotes2019001110Enabled();
    element.checkBenefitCodesValidationEnabled();

    element.updateLiteralCode();

    //Update the ICMSType control
    element.updateICMSType();

    element.updatePaymentControls();
    element.updateBenefitGroup();
    element.updateTaxationCodes();

    return ret;
}

public void generateSpedAdjustmentCode()
{
    if (!isLiteralCode)
    {
        if (FBFiscalDocumentAdjustmentCodeICMS_BR.Table52)
        {
            FBFiscalDocumentAdjustmentCodeICMS_BR.generateSpedAdjustmentCode();
        }
        else if (FBFiscalDocumentAdjustmentCodeICMS_BR.TaxType == TaxType_BR::ICMSDiff)
        {
            FBFiscalDocumentAdjustmentCodeICMS_BR.SpedAdjustmentCode = strAlpha(con2Str(spedAdjustmentCodeICMSDif));
        }
        else
        {
            FBFiscalDocumentAdjustmentCodeICMS_BR.SpedAdjustmentCode = strAlpha(con2Str(spedAdjustmentCode));
        }
    }
}

