                                    enum2int(FBFiscalDocumentAdjustmentCodeICMS_BR.Classification),
                                    FBFiscalDocumentAdjustmentCodeICMS_BR.OccurrenceCode ];

    FBFiscalDocumentAdjustmentCodeICMS_BR_ds.generateSpedAdjustmentCode();
}

