public void modified()
{
    super();

    element.checkTechNotes2019001110Enabled();
    element.checkBenefitCodesValidationEnabled();

    FBFiscalDocumentAdjustmentCodeICMS_BR.State = strUpr(FBFiscalDocumentAdjustmentCodeICMS_BR.State);

    spedAdjustmentCode = conPoke(spedAdjustmentCode, 1, FBFiscalDocumentAdjustmentCodeICMS_BR.State);
    spedAdjustmentCodeICMSDif = conPoke(spedAdjustmentCodeICMSDif, 1, FBFiscalDocumentAdjustmentCodeICMS_BR.State);

    FBFiscalDocumentAdjustmentCodeICMS_BR_ds.generateSpedAdjustmentCode();

    element.updateLiteralCode();

    if (!isLiteralCode)
    {
        FBFiscalDocumentAdjustmentCodeICMS_BR_ds.initValue();
    }

    element.updateICMSType();
    element.updateBenefitGroup();
    element.updateTaxationCodes();
