public void modified()
{
    boolean isSuppressCode = FBFiscalDocumentAdjustmentCodeICMS_BR.SuppressAdjustmentCode == NoYes::Yes;
    FBFiscalDocumentAdjustmentCodeICMS_BR_ds.object(fieldNum(FBFiscalDocumentAdjustmentCodeICMS_BR, SpedAdjustmentCode)).allowEdit(!isSuppressCode);

    if (isSuppressCode)
    {
        FBFiscalDocumentAdjustmentCodeICMS_BR.SpedAdjustmentCode = FiscalBooksConstants_BR::NULO;
    }

    super();
}

public void modified()
{
    super();

    if (techNotes2019001110Enabled)
    {
        element.updateLiteralCode();
        element.updateICMSType();
        element.updateTaxationCodes();
    }
    else
    {
        element.updateAdjustmentControls();
    }

    spedAdjustmentCode = conPoke(spedAdjustmentCode, 3, enum2int(FBFiscalDocumentAdjustmentCodeICMS_BR.AssessmentType));
    spedAdjustmentCode = conPoke(spedAdjustmentCode, 4, enum2int(FBFiscalDocumentAdjustmentCodeICMS_BR.ResponsibilityType));
    spedAdjustmentCode = conPoke(spedAdjustmentCode, 5, enum2int(FBFiscalDocumentAdjustmentCodeICMS_BR.CollectionType));
    spedAdjustmentCode = conPoke(spedAdjustmentCode, 6, enum2int(FBFiscalDocumentAdjustmentCodeICMS_BR.SourceTaxationType));

    FBFiscalDocumentAdjustmentCodeICMS_BR_ds.generateSpedAdjustmentCode();
}

