Method.Source:
[Form]
public class FBFiscalDocumentAdjustment_BR extends FormRun
{
	FBTaxAssessment_BR          fbTaxAssessment;

}

	public void init()
    {
        QueryBuildRange                         queryBuildRange;
        QueryBuildDataSource                    queryBuildDataSource;

        FiscalDocumentLine_BR                   fiscalDocumentLine;
        FiscalDocumentTaxTrans_BR                   fiscalDocumentTaxTrans;
        FiscalDocumentTaxTransOutgoingICMSDif_BR    fiscalDocTaxTransICMSDif;
        FBBookingPeriod_BR                      fbBookingPeriod;
        FBBookingPeriodFiscalDocument_BR        fbBookingPeriodFiscalDocument;
        FBBookingPeriodFiscalDocumentView_BR    fbBookingPeriodFiscalDocumentView;

        TaxType_BR                              taxType;
        TaxType_BR                              assessmentTaxType;

        super();

        GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceBR10007, funcName());

        //Initialize splitter

        switch (element.args().menuItemName())
        {
            case menuitemDisplayStr(FBFiscalDocumentAdjustmentICMS_BR):
                GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceBR10001, funcName());
                taxType = TaxType_BR::ICMS;
                break;

            case menuitemDisplayStr(FBFiscalDocumentAdjustmentICMSDiff_BR):
                GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceBR10021, funcName());
                taxType = TaxType_BR::ICMSDiff;
                break;

            case menuitemDisplayStr(FBFiscalDocumentAdjustmentICMSST_BR):
                GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceBR10003, funcName());
                taxType = TaxType_BR::ICMSST;

                break;

            default: throw error("@SYS22996");
        }

        if (element.args().dataset() == tableNum(FiscalDocumentLine_BR))
        {
            fiscalDocumentLine = element.args().record();

            queryBuildDataSource = FiscalDocumentLine_BR_ds.query().dataSourceTable(tableNum(FiscalDocumentLine_BR));

            queryBuildRange = queryBuildDataSource.addRange(fieldnum(FiscalDocumentLine_BR, RecId));
            queryBuildRange.value(queryValue(fiscalDocumentLine.RecId));
            queryBuildRange.status(RangeStatus::Locked);

            queryBuildDataSource = FiscalDocumentTaxTrans_BR_ds.query().dataSourceTable(tableNum(FiscalDocumentTaxTrans_BR));

            queryBuildRange = queryBuildDataSource.addRange(fieldnum(FiscalDocumentTaxTrans_BR, FiscalDocumentMiscCharge));
            queryBuildRange.value(queryValue(0));
            queryBuildRange.status(RangeStatus::Locked);

            queryBuildRange = queryBuildDataSource.addRange(fieldnum(FiscalDocumentTaxTrans_BR, Type));
            queryBuildRange.value(queryValue(taxType));
            queryBuildRange.status(RangeStatus::Locked);
        }
        else
        {
            throw error("@SYS22996");
        }

        if (taxType == TaxType_BR::ICMS || taxType == TaxType_BR::ICMSDiff)
        {
            if (taxType == TaxType_BR::ICMSDiff)
            {
                select firstonly RecId from  fiscalDocTaxTransICMSDif
                    exists join fiscalDocumentTaxTrans
                        where fiscalDocTaxTransICMSDif.FiscalDocumentTaxTrans == fiscalDocumentTaxTrans.RecId
                            && fiscalDocumentTaxTrans.FiscalDocumentLine == fiscalDocumentLine.RecId;

                assessmentTaxType = fiscalDocTaxTransICMSDif ? TaxType_BR::ICMSDiff : TaxType_BR::ICMS;
            }
            else
            {
                assessmentTaxType = TaxType_BR::ICMS;
            }

            select *
                from fbBookingPeriodFiscalDocument
                    where fbBookingPeriodFiscalDocument.FiscalDocument_BR == fiscalDocumentLine.FiscalDocument
                join * from fbBookingPeriod
                    where fbBookingPeriod.RecId == fbBookingPeriodFiscalDocument.FBBookingPeriod_BR
                join fbTaxAssessment
                    where fbTaxAssessment.FBBookingPeriod_BR == fbBookingPeriod.RecId
                    && fbTaxAssessment.TaxType == assessmentTaxType;
        }
        else
        {
            select FBBookingPeriod_BR, FiscalDocument_BR, ThirdPartyState, Type
                from fbBookingPeriodFiscalDocumentView
                    where fbBookingPeriodFiscalDocumentView.FiscalDocument_BR == fiscalDocumentLine.FiscalDocument
                join * from fbBookingPeriod
                    where fbBookingPeriod.RecId == fbBookingPeriodFiscalDocumentView.FBBookingPeriod_BR
                join fbTaxAssessment
                    where fbTaxAssessment.FBBookingPeriod_BR == fbBookingPeriod.RecId
                        && fbTaxAssessment.TaxType == TaxType_BR::ICMSST
                        && fbTaxAssessment.State   == fbBookingPeriodFiscalDocumentView.ThirdPartyState;
        }

        element.design().titleDatasource(FBTaxAssessment_BR_ds.id());

        LedgerTransVoucher.visible(BrazilParameters::isFBLedgerIntegrationActivated());
    }

public int active()
{
    SecurityRights curUserRights;
    boolean hasUserRights;

    int ret;

    ret = super();

    curUserRights = SecurityRights::newUser(curUserId());
    hasUserRights = curUserRights.hasMenuItemAccess(SecurableType::MenuItemDisplay, menuitemDisplayStr(LedgerTransVoucher1));
    LedgerTransVoucher.enabled(hasUserRights && FBFiscalDocumentAdjustment_BR.VoucherDataArea != '');

    return ret;
}

public void initValue()
{
    super();

    FBFiscalDocumentAdjustment_BR.FiscalDocumentTaxTrans_BR = FiscalDocumentTaxTrans_BR.RecId;
    FBFiscalDocumentAdjustment_BR.FBTaxAssessment_BR        = fbTaxAssessment.RecId;
    FBFiscalDocumentAdjustment_BR.TaxType_BR                = fbTaxAssessment.TaxType;
}

public boolean validate()
{
    boolean                                 ret;
    FBFiscalDocumentAdjustmentCodeICMS_BR   adjustmentCode;

    ret = super();

    if (BrazilParameters::isFBLedgerIntegrationActivated())
    {
        adjustmentCode = FBFiscalDocumentAdjustmentCodeICMS_BR::find(this.getValue());

        if (!adjustmentCode.Table52)
        {
            // Adjustment classification must be 'information'.
            if (fbTaxAssessment.TaxType == TaxType_BR::ICMSDiff)
            {
                if (adjustmentCode.ClassificationIcmsDif != FBGeneralClassificationICMS_BR::Information)
                {
                    ret = ret && checkFailed(strFmt("@Brazil:FBIcmsDifAdjustmentClassificationIsInvalid", FBGeneralClassificationICMS_BR::Information));
                }
            }
            else if (adjustmentCode.Classification != FBFiscalDocumentClassificationICMS_BR::Information)
            {
                ret = ret && checkFailed(strFmt("@Brazil:FBIcmsDifAdjustmentClassificationIsInvalid", FBFiscalDocumentClassificationICMS_BR::Information));
            }
        }
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    container icmsClassifications;

    if (BrazilParameters::isFBLedgerIntegrationActivated() && fbTaxAssessment.isICMSTaxAssessment())
    {
        if (fbTaxAssessment.TaxType == TaxType_BR::ICMSDiff)
        {
            icmsClassifications = [FBGeneralClassificationICMS_BR::Information];
        }
        else
        {
            icmsClassifications = [FBFiscalDocumentClassificationICMS_BR::Information];
        }
    }

    return FBFiscalDocumentAdjustmentCodeICMS_BR::lookupReferenceAdjustmentCode(_formReferenceControl, fbTaxAssessment.TaxType, fbTaxAssessment.State, icmsClassifications);
}

void clicked()
{
    Args args;
    MenuFunction menuFunction;
    TaxTrans taxTrans;

    menuFunction = new menuFunction(menuitemDisplayStr(ledgerTransVoucher), MenuItemType::Display);

    changeCompany (FBFiscalDocumentAdjustment_BR.VoucherDataArea)
    {
        select firstOnly taxTrans
            where taxTrans.Voucher     == FBFiscalDocumentAdjustment_BR.Voucher
                && taxTrans.TransDate   == FBFiscalDocumentAdjustment_BR.TransDate;

        args = new args(this);
        args.record(taxTrans);
        menuFunction.run(args);
    }
}
