}

public void init()
{
    super();

    switch (element.args().dataset())
    {
        case tableNum(FBBookingPeriod_BR) :
            this.setFromFBBookingPeriod();
            break;

        case tableNum(FBTaxAssessment_BR) :
            this.setFromFBTaxAssessment();
            break;

        case tableNum(FBSpedReinfEvent_BR) :
            this.setFromFBSpedReinfEvent();
            break;
    }
}

private void setFromFBBookingPeriod()
{
    FBBookingPeriod_BR  fbBookingPeriod;

    fbBookingPeriod = element.args().record();

    fromDate                = fbBookingPeriod.periodFrom();
    toDate                  = fbBookingPeriod.periodTo();
    fiscalEstablishmentId   = fbBookingPeriod.FiscalEstablishmentId;
    dataAreaId              = fbBookingPeriod.FiscalEstablishmentDataArea;
    bookingPeriodRecId      = fbBookingPeriod.RecId;

    topFields.visible(true);
}

private void setFromFBTaxAssessment()
{
    FBTaxAssessment_BR  fbTaxAssessment;

    fbTaxAssessment = element.args().record();

    fromDate                = fbTaxAssessment.FromDate;
    toDate                  = fbTaxAssessment.ToDate;
    fiscalEstablishmentId   = fbTaxAssessment.bookingPeriod().FiscalEstablishmentId;
    taxType                 = fbTaxAssessment.TaxType;
    state                   = fbTaxAssessment.State;
    dataAreaId              = fbTaxAssessment.BookingPeriod().FiscalEstablishmentDataArea;

    TopFields.visible(false);
}

private void setFromFBSpedReinfEvent()
{
    spedReinfEvent = element.args().record();
    FBBookingPeriod_BR fbBookingPeriod = FBBookingPeriod_BR::find(spedReinfEvent.FBBookingPeriod_BR);

    fromDate                = fbBookingPeriod.periodFrom();
    toDate                  = fbBookingPeriod.periodTo();
    fiscalEstablishmentId   = fbBookingPeriod.FiscalEstablishmentId;
    dataAreaId              = fbBookingPeriod.FiscalEstablishmentDataArea;
    bookingPeriodRecId      = fbBookingPeriod.RecId;

    topFields.visible(true);
}

private void createICMSDIFQuery(Query _query,
    FieldId _logisticsPostalAddressLink,
    QueryBuildDataSource _fiscalDocumentQbds = null,
    QueryBuildDataSource _bookingPeriodFiscalDocumentQbds = null)
{
    QueryBuildDataSource    fiscalDocumentQbds;
    QueryBuildDataSource    bookingPeriodFiscalDocumentQbds;
    QueryBuildDataSource    postalAddressQbds;
    QueryBuildDataSource    fiscalDocTaxTransOutgoingICMSDiffQbds;

    if (!_fiscalDocumentQbds)
    {
        fiscalDocumentQbds = _query.addDataSource(tableNum(FiscalDocument_BR));
    }
    else
    {
        fiscalDocumentQbds = _fiscalDocumentQbds;
    }

    fiscalDocumentQbds.addRange(fieldNum(FiscalDocument_BR, AccountingDate)).value(SysQuery::range(fromDate, toDate));
    fiscalDocumentQbds.addRange(fieldNum(FiscalDocument_BR, FiscalEstablishment)).value(SysQuery::value(fiscalEstablishmentId));

    if (!_bookingPeriodFiscalDocumentQbds)
    {
        bookingPeriodFiscalDocumentQbds = fiscalDocumentQbds.addDataSource(tableNum(FBBookingPeriodFiscalDocument_BR));
        bookingPeriodFiscalDocumentQbds.relations(true);
        bookingPeriodFiscalDocumentQbds.joinMode(JoinMode::ExistsJoin);
    }
    else
    {
        bookingPeriodFiscalDocumentQbds = _bookingPeriodFiscalDocumentQbds;
    }

    bookingPeriodFiscalDocumentQbds.addRange(fieldNum(FBBookingPeriodFiscalDocument_BR, hasIcmsdiff)).value(SysQuery::value(NoYes::Yes));

    postalAddressQbds = fiscalDocumentQbds.addDataSource(tableNum(LogisticsPostalAddress));
    postalAddressQbds.relations(false);
    postalAddressQbds.addlink(fieldNum(LogisticsPostalAddress, RecId), _logisticsPostalAddressLink);
    postalAddressQbds.joinMode(JoinMode::ExistsJoin);
    postalAddressQbds.addRange(fieldNum(LogisticsPostalAddress, State)).value(SysQuery::value(state));

    fiscalDocTaxTransOutgoingICMSDiffQbds = fiscalDocumentQbds.addDataSource(tableNum(FiscalDocumentTaxTransOutgoingICMSDif_BR));
    fiscalDocTaxTransOutgoingICMSDiffQbds.relations(true);
    fiscalDocTaxTransOutgoingICMSDiffQbds.joinMode(JoinMode::ExistsJoin);
}

public display FBFiscalDocumentSituation_BR showFiscalDocumentSituation(FiscalDocument_BR _fiscalDocument)
{
    FBBookingPeriodFiscalDocument_BR    bookingPeriodFiscalDocument = FBBookingPeriodFiscalDocument_BR::findByFiscalDocument(_fiscalDocument.RecId);

    return bookingPeriodFiscalDocument.FiscalSituation;
}

public display FBBookingPeriodDescription_BR showBookingPeriod(FiscalDocument_BR _fiscalDocument)
{
    FBBookingPeriodFiscalDocument_BR    bookingPeriodFiscalDocument = FBBookingPeriodFiscalDocument_BR::findByFiscalDocument(_fiscalDocument.RecId);
    FBBookingPeriod_BR                  bookingPeriod;

    bookingPeriod = bookingPeriodFiscalDocument.bookingPeriod();

    if (bookingPeriod)
    {
        return bookingPeriod.showBookingPeriod();
    }

    return '';
}

public void doAudit(boolean _audit)
{
    FiscalDocument_BR fiscalDocument;

    for (fiscalDocument = getFirstSelection(FiscalDocument_BR_DS);
         fiscalDocument;
         fiscalDocument = FiscalDocument_BR_DS.getNext())
    {
        FiscalDocument_BR::doAudit(_audit, fiscalDocument);
        FiscalDocument_BR_ds.reread();
    }
}

private Query createQueryFromFBTaxAssessment()
{
    Query                   query;
    QueryBuildDataSource    qbds, fiscalDocumentQbds;
    FiscalOrganization_BR   fiscalOrganization;

    utcdatetime validFrom = DateTimeUtil::minValue();
    utcdatetime validTo   = DateTimeUtil::maxValue();

    query = new Query();
    query.allowCrossCompany(true);
    query.addCompanyRange(dataAreaId);

    fiscalDocumentQbds = query.addDataSource(tableNum(FiscalDocument_BR));
    fiscalDocumentQbds.addRange(fieldNum(FiscalDocument_BR, AccountingDate)).value(SysQuery::range(fromDate, toDate));

    qbds = fiscalDocumentQbds.addDataSource(tableNum(FBBookingPeriodFiscalDocument_BR));
    qbds.relations(true);
    qbds.joinMode(JoinMode::ExistsJoin);

    switch (taxType)
    {
        case TaxType_BR::ICMS:
            fiscalDocumentQbds.addRange(fieldNum(FiscalDocument_BR, FiscalEstablishment)).value(SysQuery::value(fiscalEstablishmentId));

            qbds.addRange(fieldNum(FBBookingPeriodFiscalDocument_BR, hasIcms)).value(SysQuery::value(NoYes::Yes));
            break;

        case TaxType_BR::ICMSST:
            fiscalDocumentQbds.addRange(fieldNum(FiscalDocument_BR, FiscalEstablishment)).value(SysQuery::value(fiscalEstablishmentId));

            qbds.addRange(fieldNum(FBBookingPeriodFiscalDocument_BR, hasIcmsst)).value(SysQuery::value(NoYes::Yes));

            qbds = query.dataSourceTable(tableNum(FiscalDocument_BR));
            qbds = qbds.addDataSource(tableNum(LogisticsPostalAddress));
            qbds.relations(false);
            qbds.addLink(fieldNum(LogisticsPostalAddress, RecId), fieldNum(FiscalDocument_BR, ThirdPartyPostalAddress));
            qbds.joinMode(JoinMode::ExistsJoin);
            qbds.addRange(fieldNum(LogisticsPostalAddress, State)).value(SysQuery::value(state));
            query.validTimeStateDateTimeRange(validFrom, validTo);
            break;

        case TaxType_BR::ICMSDiff:
            query.queryType(QueryType::Union);

            this.createICMSDIFQuery(query, fieldNum(FiscalDocument_BR, FiscalEstablishmentPostalAddress), fiscalDocumentQbds, qbds);
            this.createICMSDIFQuery(query, fieldNum(FiscalDocument_BR, ThirdPartyPostalAddress));
            break;

        case TaxType_BR::IPI:
            fiscalDocumentQbds.addRange(fieldNum(FiscalDocument_BR, FiscalEstablishment)).value(SysQuery::value(fiscalEstablishmentId));

            qbds.addRange(fieldNum(FBBookingPeriodFiscalDocument_BR, hasIpi)).value(SysQuery::value(NoYes::Yes));
            break;

        case TaxType_BR::ISS:
            fiscalDocumentQbds.addRange(fieldNum(FiscalDocument_BR, FiscalEstablishment)).value(SysQuery::value(fiscalEstablishmentId));

            qbds.addRange(fieldNum(FBBookingPeriodFiscalDocument_BR, hasIss)).value(SysQuery::value(NoYes::Yes));
            break;

        case TaxType_BR::PIS:
        case TaxType_BR::COFINS:
            qbds.addRange(fieldNum(FBBookingPeriodFiscalDocument_BR, HasPis)).value(SysQuery::value(NoYes::Yes));
            qbds.addRange(fieldNum(FBBookingPeriodFiscalDocument_BR, HasCofins)).value(SysQuery::value(NoYes::Yes));

            fiscalOrganization = FiscalOrganization_BR::findByFiscalEstablishmentId(fiscalEstablishmentId, dataAreaId);

            qbds = fiscalDocumentQbds.addDataSource(tableNum(FiscalEstablishment_BR));
            qbds.relations(true);
            qbds.addRange(fieldNum(FiscalEstablishment_BR, FiscalOrganization_BR)).value(SysQuery::value(fiscalOrganization.RecId));
            qbds.joinMode(JoinMode::ExistsJoin);
            break;

        case TaxType_BR::INSSCPRB :
            qbds = fiscalDocumentQbds.addDataSource(tableNum(FBTaxTrans_BR));
            qbds.relations(true);
            findOrCreateRange_W(qbds, fieldNum(FBTaxTrans_BR, TaxType), SysQuery::value(TaxType_BR::INSSCPRB)).status(RangeStatus::Locked);
            qbds.joinMode(JoinMode::ExistsJoin);
            break;
    }

    return query;
}

private Query createQueryFromFBBookingPeriod()
{
    Query                   query;
    QueryBuildDataSource    qbds;
    QueryBuildDataSource    fiscalDocumentDS;

    query = new Query();
    query.allowCrossCompany(true);
    query.addCompanyRange(dataAreaId);

    fiscalDocumentDS = query.addDataSource(tableNum(FiscalDocument_BR));
    fiscalDocumentDS.addRange(fieldNum(FiscalDocument_BR, AccountingDate)).value(SysQuery::range(fromDate, toDate));
    fiscalDocumentDS.addRange(fieldNum(FiscalDocument_BR, FiscalEstablishment)).value(SysQuery::value(fiscalEstablishmentId));

    this.addConditionAllBookedNotBooked(query);
    if (AllBookedNotbooked.selection() == FBAllBookedNotbooked_BR::Booked)
    {
        qbds = query.dataSourceTable(tableNum(FBBookingPeriodFiscalDocument_BR));
        qbds.addRange(fieldNum(FBBookingPeriodFiscalDocument_BR, FBBookingPeriod_BR)).value(SysQuery::value(bookingPeriodRecId));
    }    

    this.addConditionFBAllAuditedNotAudited(query);

    return query;
}

private void addConditionAllBookedNotBooked(Query _fiscalDocumentQuery)
{
    QueryBuildDataSource fiscalDocumentDS = _fiscalDocumentQuery.dataSourceTable(tableNum(FiscalDocument_BR));
    QueryBuildDataSource qbds;

    switch (AllBookedNotbooked.selection())
    {
        case FBAllBookedNotbooked_BR::All:
            break;

        case FBAllBookedNotbooked_BR::Booked:
            qbds = fiscalDocumentDS.addDataSource(tableNum(FBBookingPeriodFiscalDocument_BR));
            qbds.relations(true);
            qbds.joinMode(JoinMode::ExistsJoin);                    
            break;

        case FBAllBookedNotbooked_BR::NotBooked:
            qbds = fiscalDocumentDS.addDataSource(tableNum(FBBookingPeriodFiscalDocument_BR));
            qbds.relations(true);
            qbds.joinMode(JoinMode::NoExistsJoin);
            break;
    }
}

        private void addConditionFBAllAuditedNotAudited(Query _fiscalDocumentQuery)
		{
            QueryBuildDataSource fiscalDocumentDS = _fiscalDocumentQuery.dataSourceTable(tableNum(FiscalDocument_BR));

            switch (FBAllAuditedNotAudited.selection())
            {
                case AllAuditedNotAudited_BR::All :
                    break;

                case AllAuditedNotAudited_BR::Audited :
                    SysQuery::findOrCreateRange(fiscalDocumentDS, fieldNum(FiscalDocument_BR, AuditedBy)).value(SysQuery::valueNotEmptyString());
                    break;

                case AllAuditedNotAudited_BR::NotAudited :
                    SysQuery::findOrCreateRange(fiscalDocumentDS, fieldNum(FiscalDocument_BR, AuditedBy)).value(SysQuery::valueEmptyString());
                    break;
            }
		}

public void executeQuery()
{
    Query query;

    switch (element.args().dataset())
    {
        case tableNum(FBBookingPeriod_BR) :
            query = this.createQueryFromFBBookingPeriod();
            break;

        case tableNum(FBTaxAssessment_BR) :
            query = this.createQueryFromFBTaxAssessment();
            break;

        case tableNum(FBSpedReinfEvent_BR) :
            query = this.createQueryFromFBSpedReinfEvent();
            break;
    }

    if (query)
    {
        this.query(query);
    }

    super();
}

private Query createQueryFromFBSpedReinfEvent()
{
    Query                   query;
    QueryBuildDataSource    fiscalDocumentDS;
    QueryBuildDataSource    qbdsEventFiscalDocument;

    query = new Query();
    query.allowCrossCompany(true);
    query.addCompanyRange(dataAreaId);

    fiscalDocumentDS = query.addDataSource(tableNum(FiscalDocument_BR));

    this.addConditionAllBookedNotBooked(query);
    this.addConditionFBAllAuditedNotAudited(query);

    qbdsEventFiscalDocument = fiscalDocumentDS.addDataSource(tableNum(FBSpedReinfEventFiscalDocument_BR));
    qbdsEventFiscalDocument.relations(true);
    qbdsEventFiscalDocument.joinMode(JoinMode::ExistsJoin);
    SysQuery::findOrCreateRange(qbdsEventFiscalDocument, fieldNum(FBSpedReinfEventFiscalDocument_BR, FBSpedReinfEvent_BR)).value(SysQuery::value(spedReinfEvent.RecId));

    return query;
}

