/// <summary>
/// Checks if the credit source code field shoud be enabled.
/// </summary>
/// <param name="_taxType">
/// Tax type of the current tax transaction.
/// </param>
/// <param name="_taxationCode">
/// Taxation code of the current tax transaction.
/// </param>
/// <param name="_isCreateOrUpdate">
/// Indicates if the current operation is create or update a tax transaction.
/// </param>
public void checkEnableCreditSourceCode(TaxType_BR _taxType, TaxationCode_BR _taxationCode, boolean _isCreateOrUpdate)
{
    if (    _taxType == TaxType_BR::PIS
        &&  (   (str2int(_taxationCode) >= 50 &&  str2int(_taxationCode) <= 56)
            ||  (str2int(_taxationCode) >= 60 &&  str2int(_taxationCode) <= 66))
        )
    {
        isPisCredit     = _isCreateOrUpdate;
    }
    else if (   _taxType == TaxType_BR::COFINS
        &&  (   (str2int(_taxationCode) >= 50 &&  str2int(_taxationCode) <= 56)
            ||  (str2int(_taxationCode) >= 60 &&  str2int(_taxationCode) <= 66))
        )
    {
        isCofinsCredit  = _isCreateOrUpdate;
    }

    if (isPisCredit && isCofinsCredit)
    {
        Details_CreditSourceCode.enabled(true);
    }
    else
    {
        Details_CreditSourceCode.enabled(false);
    }
}

