private Query createQueryFromFBBookingPeriod(FBBookingPeriod_BR fbBookingPeriod)
{
    Query                   query;
    QueryBuildDataSource    qbds;

    query = new Query();
    query.allowCrossCompany(true);

    qbds = query.addDataSource(tableNum(FBNonFiscalOperation_BR));
    qbds.addRange(fieldNum(FBNonFiscalOperation_BR, FBBookingPeriod_BR)).value(SysQuery::value(fbBookingPeriod.RecId));

    return query;
}

private Query createQueryFromFBSpedReinfEvent(FBSpedReinfEvent_BR _spedReinfEvent)
{
    Query query = new Query();
    query.allowCrossCompany(true);

    QueryBuildDataSource qbdsNFO = query.addDataSource(tableNum(FBNonFiscalOperation_BR));

    QueryBuildDataSource qbdsEventNFO = qbdsNFO.addDataSource(tableNum(FBSpedReinfEventNonFiscalOperation_BR));
    qbdsEventNFO.relations(true);
    qbdsEventNFO.joinMode(JoinMode::ExistsJoin);
    qbdsEventNFO.addRange(fieldNum(FBSpedReinfEventNonFiscalOperation_BR, FBSpedReinfEvent_BR)).value(SysQuery::value(_spedReinfEvent.RecId));

    return query;
}

public void executeQuery()
{
    Query query;

    switch (element.args().dataset())
    {
        case tableNum(FBBookingPeriod_BR):
            query = this.createQueryFromFBBookingPeriod(element.args().record());
            break;

        case tableNum(FBTaxAssessment_BR):
            query = this.createQueryFromFBTaxAssessment(element.args().record());
            break;

        case tableNum(FBSpedReinfEvent_BR):
            query = this.createQueryFromFBSpedReinfEvent(element.args().record());
            break;
    }

    if (query)
    {
        this.query(query);
    }

    super();
}

public display FBBookingPeriodDescription_BR showBookingPeriod(FBNonFiscalOperation_BR fbNonFiscalOperation)
{
    FBBookingPeriod_BR                  bookingPeriod;

    bookingPeriod = FBBookingPeriod_BR::find(fbNonFiscalOperation.FBBookingPeriod_BR);

    if (bookingPeriod)
    {
        return bookingPeriod.showBookingPeriod();
    }

    return '';
}

public int active()
{
    int ret;
    FBNonFiscalOperationTaxTrans_BR taxTrans;            

    ret = super();

    priorAccountType = FBNonFiscalOperation_BR.AccountType;

    // Check if credit source code field shoud be enable or disabled.
    // It is enabled when the PIS and COFINS taxation code are between 50 and 66.
    Details_CreditSourceCode.enabled(false);

    isPisCredit     = false;
    isCofinsCredit  = false;

    select RecId, TaxationCode from taxTrans
        where   taxTrans.FBNonFiscalOperation_BR == FBNonFiscalOperation_BR.RecId
            &&  taxTrans.Type                    == TaxType_BR::PIS;

    if (taxTrans)
    {
        element.checkEnableCreditSourceCode(TaxType_BR::PIS, taxTrans.TaxationCode, true);
    }

    select RecId, TaxationCode from taxTrans
        where   taxTrans.FBNonFiscalOperation_BR == FBNonFiscalOperation_BR.RecId
            &&  taxTrans.Type                    == TaxType_BR::COFINS;

    if (taxTrans)
    {
        element.checkEnableCreditSourceCode(TaxType_BR::COFINS, taxTrans.TaxationCode, true);
    }

    // Check if the non fiscal operation is editable.
    if (FBNonFiscalOperation_BR.Voucher || FBNonFiscalOperation_BR.FBBookingPeriod_BR)
    {
        // Non fiscal operations with voucher should not be deleted.
        if (FBNonFiscalOperation_BR.existsVoucher())
        {
            FBNonFiscalOperation_BR_ds.allowDelete(false);
            LedgerVoucherTrans.enabled(true);
        }
        else
        {
            FBNonFiscalOperation_BR_ds.allowDelete(true);
            LedgerVoucherTrans.enabled(false);
        }

        // It is not allowd to edit a non fiscal operation when it is booked or when it was created from general ledger.
        DetailsLeft.allowEdit(false);

        Details_TotalAmount.allowEdit(false);
        Details_OffsetLedgerDimension.allowEdit(false);
        Details_OffsetDimensionValue.allowEdit(false);

        // It is also necessary to disable the tax transactions.
        LineStripNew.enabled(false);
        LineStripDelete.enabled(false);
        TaxGrid.allowEdit(false);
    }
    else
    {
        FBNonFiscalOperation_BR_ds.allowDelete(true);
        LedgerVoucherTrans.enabled(false);

        // When the non fiscal operation is not booked or was not created from general ledger, all fields should be editable.
        DetailsLeft.allowEdit(true);
        // DetailsRight
        Details_TotalAmount.allowEdit(true);
        Details_OffsetLedgerDimension.allowEdit(true);
        Details_OffsetDimensionValue.allowEdit(true);

        LineStripNew.enabled(true);
        LineStripDelete.enabled(true);
        TaxGrid.allowEdit(true);
    }

    Details_AccountNum.parmDataAreaId(FBNonFiscalOperation_BR.FiscalEstablishmentDataArea ? FBNonFiscalOperation_BR.FiscalEstablishmentDataArea : curext());

    this.removeAccountTypes();            

    return ret;
}

private void removeAccountTypes()
{
    DictEnum accountTypes = new DictEnum(enumNum(LedgerJournalACType));

    for (int counter = 0; counter < accountTypes.values(); counter++)
    {
        if (!conFind(FBNonFiscalOperation_BR::nonFiscalOperationAccountTypes(), accountTypes.index2Value(counter)))
        {
            Grid_AccountType.delete(accountTypes.index2Name(counter));
            Details_AccountType.delete(accountTypes.index2Name(counter));
        }
    }
}

