private Query createQueryFromFBTaxAssessment(FBTaxAssessment_BR fbTaxAssessment)
{
    Query                   query;
    QueryBuildDataSource    qbds, nonFiscalOrganizationQbds;
    FiscalOrganization_BR   fiscalOrganization;
    FBBookingPeriod_BR      fbBookingPeriod;

    fbBookingPeriod     = fbTaxAssessment.bookingPeriod();
    fiscalOrganization  = FiscalOrganization_BR::findByFiscalEstablishmentId(fbBookingPeriod.FiscalEstablishmentId, fbBookingPeriod.FiscalEstablishmentDataArea);

    query = new Query();
    query.allowCrossCompany(true);

    nonFiscalOrganizationQbds = query.addDataSource(tableNum(FBNonFiscalOperation_BR));
    nonFiscalOrganizationQbds.addRange(fieldNum(FBNonFiscalOperation_BR, TransDate)).value(SysQuery::range(fbBookingPeriod.periodFrom(), fbBookingPeriod.periodTo()));
    nonFiscalOrganizationQbds.addRange(fieldNum(FBNonFiscalOperation_BR, FBBookingPeriod_BR)).value(SysQuery::valueNot(0));

    qbds = nonFiscalOrganizationQbds.addDataSource(tableNum(FiscalEstablishment_BR));
    qbds.relations(true);
    qbds.addRange(fieldNum(FiscalEstablishment_BR, FiscalOrganization_BR)).value(SysQuery::value(fiscalOrganization.RecId));
    qbds.joinMode(JoinMode::ExistsJoin);

    qbds = nonFiscalOrganizationQbds.addDataSource(tableNum(FBNonFiscalOperationTaxTrans_BR));
    qbds.addRange(fieldNum(FBNonFiscalOperationTaxTrans_BR, Type)).value(SysQuery::value(fbTaxAssessment.TaxType));
    qbds.relations(true);
    qbds.joinMode(JoinMode::ExistsJoin);

    return query;
}

