public void lookup()
{
    switch (FBNonFiscalOperation_BR.AccountType)
    {
        case LedgerJournalACType::Cust:
            CustTable::lookupCustomer(  Details_AccountNum,
                                        FBNonFiscalOperation_BR.FiscalEstablishmentDataArea ? FBNonFiscalOperation_BR.FiscalEstablishmentDataArea : curext());
            break;

        case LedgerJournalACType::Vend:
            VendTable::lookupVendor(Details_AccountNum,
                                    FBNonFiscalOperation_BR.FiscalEstablishmentDataArea ? FBNonFiscalOperation_BR.FiscalEstablishmentDataArea : curext());
            break;

        default:
            BankAccountTable::lookupBankAccount(Details_AccountNum,
                                                FBNonFiscalOperation_BR.FiscalEstablishmentDataArea ? FBNonFiscalOperation_BR.FiscalEstablishmentDataArea : curext());
            break;
    }
}

