[Form]
public class FBParametersPerState_BR extends FormRun
{
}

public void init()
{
    FBParametersPerState_BR::init();

    super();
    //Initialize splitter

    PresumedTaxTabPage.visible(BrazilParameters::find().ICMSPresumedTax == NoYes::Yes);
    PresumedCreditTabPage.visible(EinvoicingPresumedCreditFlight_BR::instance().isEnabled() && BrazilParameters::find().ICMSPresumedCredit == NoYes::Yes);
}

public void setLookupButton()
{
    if (ICMSSTRestitutionSetupData_BR.CFOPCode == TableGroupAll::All)
    {
        ICMSSTRestitutionSetupData_BR_ds.object(fieldnum(ICMSSTRestitutionSetupData_BR, CFOPRelation)).allowEdit(false);
        ICMSSTRestitutionSetupData_BR_ds.object(fieldnum(ICMSSTRestitutionSetupData_BR, CFOPRelation)).skip(true);
        ICMSSTRestitutionSetupData_BR.CFOPRelation = '';
    }
}

public int active()
{
    int ret;

    ret = super();

    FBParametersPerState_BR_ds.updateDesign();

    return ret;
}

public void updateDesign()
{
    if (FBParametersPerState_BR.ICMSAdjustmentByFiscalDocument)
    {
        FBParametersPerState_BR_ds.object(fieldNum(FBParametersPerState_BR, ICMSFiscalDocumentAdjustmentCode)).visible(true);
        FBParametersPerState_BR_ds.object(fieldNum(FBParametersPerState_BR, ICMSFiscalDocumentAdjCodeFixedAsset)).visible(true);
        FBParametersPerState_BR_ds.object(fieldNum(FBParametersPerState_BR, ICMSFBObservationCodeTable)).visible(true);
        FBParametersPerState_BR_ds.object(fieldNum(FBParametersPerState_BR, ICMSGeneralAdjustmentCode)).visible(false);
        FBParametersPerState_BR_ds.object(fieldNum(FBParametersPerState_BR, ICMSGeneralAdjustmentCodeFixedAsset)).visible(false);
    }
    else
    {
        FBParametersPerState_BR_ds.object(fieldNum(FBParametersPerState_BR, ICMSFiscalDocumentAdjustmentCode)).visible(false);
        FBParametersPerState_BR_ds.object(fieldNum(FBParametersPerState_BR, ICMSFiscalDocumentAdjCodeFixedAsset)).visible(false);
        FBParametersPerState_BR_ds.object(fieldNum(FBParametersPerState_BR, ICMSFBObservationCodeTable)).visible(false);
        FBParametersPerState_BR_ds.object(fieldNum(FBParametersPerState_BR, ICMSGeneralAdjustmentCode)).visible(true);
        FBParametersPerState_BR_ds.object(fieldNum(FBParametersPerState_BR, ICMSGeneralAdjustmentCodeFixedAsset)).visible(true);
    }

    element.control(element.controlId(formControlStr(FBParametersPerState_BR, SPCAT66))).visible(
        FBParametersPerState_BR.StateId == FiscalBooksConstants_BR::StateSP &&
        FeatureStateProvider::isFeatureEnabled(FBSpedFiscalSPCAT66Feature_BR::instance()));

    boolean inRS = FBParametersPerState_BR.checkStateIdForBlock1900();

    SubassessmentParametersTabPage.visible(inRS);
    SubassessmentParameters_Block1900.enabled(inRS);

    SubassessmentParameters_IsRetailCompany.enabled(inRS);
    SubassessmentParameters_FBSubTaxAssessment_BR.enabled(inRS);
    SubassessmentParameters_ICMSGeneralAdjustmentCodeTaxableIncoming.enabled(inRS);
    SubassessmentParameters_ICMSGeneralAdjustmentCodeTaxableOutgoing.enabled(inRS);
    SubassessmentParameters_ICMSGeneralAdjustmentCodeCreditReversal.enabled(inRS && FBParametersPerState_BR.IsRetailCompany);
    SubassessmentParameters_ICMSGeneralAdjustmentCodeCreditInventory.enabled(inRS && FBParametersPerState_BR.IsRetailCompany);
    SubassessmentParameters_ICMSGeneralAdjustmentCodeComplement.enabled(inRS);
    SubassessmentParameters_ICMSGeneralAdjustmentCodeRestitution.enabled(inRS);
    SubassessmentParameters_ICMSGeneralAdjustmentCodeComplementE220.enabled(inRS);
    SubassessmentParameters_ICMSGeneralAdjustmentCodeRestitutionE111.enabled(inRS);
    SubassessmentParameters_SubTaxAssessmentDescription.enabled(inRS);
    SubassessmentParameters_SubTaxAssesmentLedgerJournalNameId.enabled(inRS);
    SubassessmentParameters_Consolidate1923ForOutgoing.enabled(inRS);

    Resolution.visible(FBParametersPerState_BR.StateId == FiscalBooksConstants_BR::StateRJ);

    SPEDFiscal_GenerateC176Record.visible(inRS);
    ICMSSTRestitutionSetup.visible(inRS);
    ISMSSTRestitutionSetupGrid.enabled(inRS && FBParametersPerState_BR.GenerateC176Record);
    ISMSSTRestitutionActionPane.enabled(inRS && FBParametersPerState_BR.GenerateC176Record);
}

public display Name stateName(FBParametersPerState_BR _parametersPerState)
{
    return LogisticsAddressState::find(_parametersPerState.CountryRegionId, _parametersPerState.StateId).Name;
}

public void initValue()
{
    super();

    FBParametersPerState_BR.CountryRegionId = DirParty::primaryPostalAddress(Companyinfo::current()).CountryRegionId;
}

public void modified()
{
    super();

    FBParametersPerState_BR_ds.updateDesign();
}

public void modified()
{
    super();

    FBParametersPerState_BR_ds.updateDesign();
}

public void modified()
{
    super();

    FBParametersPerState_BR_ds.updateDesign();
}

public void modified()
{
    super();

    FBParametersPerState_BR_ds.updateDesign();

    if (FBParametersPerState_BR.ICMSAdjustmentByFiscalDocument)
    {
        FBParametersPerState_BR.ICMSGeneralAdjustmentCode = 0;
        FBParametersPerState_BR.ICMSGeneralAdjustmentCodeFixedAsset = 0;
    }
    else
    {
        FBParametersPerState_BR.ICMSFiscalDocumentAdjustmentCode = 0;
        FBParametersPerState_BR.ICMSFiscalDocumentAdjCodeFixedAsset = 0;
        FBParametersPerState_BR.ICMSFBObservationCodeTable = 0;
    }
}

